/*__ Red Animation _________________________________________________________*/
.ytp-swatch-background-color,
.ytp-swatch-background-color-secondary
{
	animation: AnimationName 10s ease infinite!important;
	background: linear-gradient(to bottom, #ff0004 0%, #000000 50%, #ff0004 100%)!important;
	background-size: 40% 40%!important;
}

@-webkit-keyframes AnimationName
{
	0%
	{
		background-position: 0% 50%
	}

	50%
	{
		background-position: 100% 50%
	}

	100%
	{
		background-position: 0% 50%
	}
}

@-moz-keyframes AnimationName
{
	0%
	{
		background-position: 0% 50%
	}

	50%
	{
		background-position: 100% 50%
	}

	100%
	{
		background-position: 0% 50%
	}
}

@keyframes AnimationName
{
	0%
	{
		background-position: 0% 50%
	}

	50%
	{
		background-position: 100% 50%
	}

	100%
	{
		background-position: 0% 50%
	}
}

/*__ Green Animation ______________________________________________________*/
.ytp-load-progress
{
	animation: gray 10s ease infinite!important;
	background: linear-gradient(90deg, #0f0, #000, #0f0)!important;
	background-size: 40% 40%!important;
}

@-webkit-keyframes gray
{
	0%
	{
		background-position: 0% 50%
	}

	50%
	{
		background-position: 100% 50%
	}

	100%
	{
		background-position: 0% 50%
	}
}

@-moz-keyframes gray
{
	0%
	{
		background-position: 0% 50%
	}

	50%
	{
