css:
@-moz-document domain("youtube.com") {
/* Scrollbar Settings */

 ::-webkit-scrollbar {
 	/* Width */
 	width: 1.2em!important;
 }

 /* Color of handle ___________________________________________________________________________*/

 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track ______________________________________________________________________________*/

 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom ___________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. ______________*/

::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

}

@-moz-document domain("youtube.com") {
/* Site Settings */

* {
	text-decoration: none!important;
}

.strapline.ytmusic-carousel-shelf-basic-header-renderer,
#guide-section-title.ytd-guide-section-renderer,
.badge-style-type-collection.ytd-badge-supported-renderer,
.less-button.ytd-video-secondary-info-renderer,
.more-button.ytd-video-secondary-info-renderer,
tp-yt-paper-tabs.ytd-topic-channel-details-renderer,
ytd-button-renderer
{
	text-transform: capitalize!important;
}

#container.ytd-searchbox,
#search-icon-legacy.ytd-searchbox,
:focus,
ytd-button-renderer.style-white-with-border[is-paper-button]
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
}

#text.ytd-channel-name {
	white-space: pre-line;
}

#collapsible,
.title-group.ytmusic-two-row-item-renderer
{
	display: block!important;
}

xtp-yt-paper-button[hidden] {
	display: inline-block!important;
}

/*___ Fonts ________________________________________________________________________*/

#description-text.ytd-video-renderer,
#title.ytd-shelf-renderer,
tp-yt-paper-tabs.ytd-topic-channel-details-renderer
{
	font-size: 21px!important;
}

.description.ytmusic-detail-header-renderer,
#content-text.ytd-comment-renderer,
#description.ytd-channel-video-player-renderer,
#subtitle.ytd-shelf-renderer,
#text.ytd-channel-name,
ytd-expander.ytd-video-secondary-info-renderer
{
	font-size: 19px!important;
}

#metadata-line.ytd-video-meta-block
{
	font-size: 17px!important;
}

selector
{
	font-size: 15px!important;
}

#description-text.ytd-video-renderer,
#description.ytd-channel-video-player-renderer,
#subtitle.ytd-shelf-renderer,
ytd-expander.ytd-video-secondary-info-renderer
{
	line-height: 25px!important;
}

/*___ Remove Items _________________________________________________________________*/

selector
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

.ytp-right-controls {
    margin-right: 11px;
}

.ytp-progress-bar-container, .ytp-exp-bigger-button-like-mobile.ytp-small-mode .ytp-progress-bar-container {
    bottom: 46px;
}

/*___ Margins ______________________________________________________________________*/

ytmusic-responsive-list-item-renderer,
#contents.style-scope.ytmusic-section-list-renderer,
#contents.ytmusic-section-list-renderer > ytmusic-carousel-shelf-renderer.ytmusic-section-list-renderer:not(:last-child),
#contents.ytmusic-section-list-renderer > ytmusic-immersive-carousel-shelf-renderer.ytmusic-section-list-renderer:not(:last-child),
#primary.ytd-watch-flexy,
ytd-destination-button-renderer,
ytd-grid-video-renderer,
ytd-playlist-video-renderer,
ytd-search,
ytd-two-column-browse-results-renderer.grid:not(.grid-disabled),
ytd-watch-flexy[flexy] #secondary.ytd-watch-flexy
{
	margin: 12px!important;
}

selector
{
	margin: 3px 6px!important;
}

/*___ Paddings _____________________________________________________________________*/

ytmusic-responsive-list-item-renderer,
#contents.ytd-rich-grid-renderer,
#primary.ytd-watch-flexy,
ytd-comment-thread-renderer,
ytd-grid-video-renderer,
ytd-two-column-browse-results-renderer.grid:not(.grid-disabled),
ytd-video-renderer,
ytd-watch-flexy[flexy] #secondary.ytd-watch-flexy
{
	padding: 12px!important;
}

selector
{
	padding: 3px 6px!important;
}

/*___ Heights ______________________________________________________________________*/

#description-text.ytd-video-renderer,
#subtitle.ytd-shelf-renderer,
.title-group.ytmusic-two-row-item-renderer,
ytd-expander[collapsed] > #content.ytd-expander,
ytmusic-menu-popup-renderer
{
	max-height: none!important;
}

ytd-expander.ytd-video-secondary-info-renderer
{
	height: auto!important;
}

.title.ytmusic-two-row-item-renderer,
.title-group.ytmusic-two-row-item-renderer
{
    height: 86px!important;
}
/*___ Widths _______________________________________________________________________*/

#content.ytd-channel-video-player-renderer,
#description.ytd-video-secondary-info-renderer,
#subtitle.ytd-shelf-renderer,
.text-wrapper.ytd-video-renderer,
ytd-metadata-row-header-renderer.ytd-metadata-row-container-renderer,
ytd-metadata-row-renderer.ytd-metadata-row-container-renderer,
ytd-metadata-with-image-row-renderer.ytd-metadata-row-container-renderer,
ytd-two-column-browse-results-renderer.grid:not(.grid-disabled),
ytmusic-menu-popup-renderer
{
	max-width: none!important;
}

#items.ytmusic-carousel > .ytmusic-carousel[aspect-ratio=MUSIC_TWO_ROW_ITEM_THUMBNAIL_ASPECT_RATIO_RECTANGLE_16_9],
ytd-destination-button-renderer
{
	width: auto!important;
}

#contents.style-scope.ytmusic-section-list-renderer,
.grid-6-columns.ytd-two-column-browse-results-renderer,
ytd-two-column-browse-results-renderer.grid-6-columns,
ytd-two-column-browse-results-renderer.grid:not(.grid-disabled),
ytmusic-carousel-shelf-renderer
{
	width: 97%!important;
}

}

@-moz-document domain("youtube.com") {
/* Font Settings */

a[href],
td span
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.less-button.ytd-video-secondary-info-renderer:hover,
.more-button.ytd-video-secondary-info-renderer:hover,
a[href]:hover,
h1,
input[type="checkbox"]:checked+label,input[type="radio"]:checked+label,
tp-yt-paper-tab.iron-selected.ytd-topic-channel-details-renderer,
yt-chip-cloud-chip-renderer[chip-style=STYLE_DEFAULT]:hover #text.yt-chip-cloud-chip-renderer,
ytd-guide-entry-renderer:hover .title.ytd-guide-entry-renderer,
ytd-mini-guide-entry-renderer:hover .title.ytd-mini-guide-entry-renderer
 {
	color: #ff0!important;
}

/*__________________________________________________________________________________*/

.description.ytmusic-detail-header-renderer,
#content-text.ytd-comment-renderer,
#content.ytd-channel-video-player-renderer,
#description-text.ytd-video-renderer,
#icon-label.yt-dropdown-menu,
#label-text.yt-dropdown-menu,
#label.ytd-playlist-add-to-option-renderer,
#label.ytd-privacy-dropdown-item-renderer,
#metadata-line.ytd-grid-video-renderer,
#owner-sub-count.ytd-video-owner-renderer,
#stats.ytd-playlist-sidebar-primary-info-renderer,
#subtitle.ytd-shelf-renderer,
#title-annotation.ytd-shelf-renderer,
.badge-style-type-simple.ytd-badge-supported-renderer,
.content.ytd-metadata-row-renderer,
.content.ytd-video-secondary-info-renderer,
.text.ytd-notification-renderer,
.title.ytd-mini-guide-entry-renderer,
a.ytmusic-content-update-chip,
li,
p,
span.ytd-video-view-count-renderer,
tp-yt-paper-input-char-counter,
tp-yt-paper-tabs.ytd-topic-channel-details-renderer,
yt-chip-cloud-chip-renderer[chip-style=STYLE_DEFAULT],
yt-chip-cloud-chip-renderer[chip-style=STYLE_HOME_FILTER],
yt-chip-cloud-chip-renderer[chip-style=STYLE_REFRESH_TO_NOVEL_CHIP],
yt-formatted-string.ytd-comments-header-renderer,
yt-formatted-string.ytd-sub-feed-option-renderer,
ytd-button-renderer.style-default[is-icon-button] #text.ytd-button-renderer,
ytd-button-renderer.style-opacity[is-icon-button] #text.ytd-button-renderer,
ytd-button-renderer.style-text[is-icon-button] #text.ytd-button-renderer,
ytd-comment-action-buttons-renderer,
ytd-compact-link-renderer[compact-link-style=compact-link-style-type-history-my-activity-link] #content-icon.ytd-compact-link-renderer[hidden] + #primary-text-container.ytd-compact-link-renderer > #label.ytd-compact-link-renderer,
ytd-inline-form-renderer[component-style=INLINE_FORM_STYLE_BODY_TEXT_PLACEHOLDER] #text-displayed.ytd-inline-form-renderer,
ytd-profile-column-stats-entry-renderer,
ytd-toggle-button-renderer.style-text[is-icon-button] #text.ytd-toggle-button-renderer
 {
	color: #fff!important;
}

/*___ Khaki ________________________________________________________________________*/

.title.ytmusic-shelf-renderer > yt-formatted-string.ytmusic-shelf-renderer,
#guide-section-title.ytd-guide-section-renderer,
#title.ytd-add-to-playlist-renderer,
#title.ytd-shelf-renderer,
#video-title.ytd-rich-grid-media,
.content.ytd-metadata-row-header-renderer,
.title.ytmusic-carousel-shelf-basic-header-renderer,
h1,
h2,
ytd-inline-form-renderer[component-style=INLINE_FORM_STYLE_TITLE] #text-displayed.ytd-inline-form-renderer
 {
	color: #F0E890!important;
}

/*___ Burlywood ____________________________________________________________________*/

.badge-style-type-collection.ytd-badge-supported-renderer,
.strapline.ytmusic-carousel-shelf-basic-header-renderer,
h3,
h4
 {
	color: #e0b880!important;
}

/*___ Sandy Brown __________________________________________________________________*/

label.tp-yt-paper-dropdown-menu-light,
h5,
h6,
label
 {
	color: #F0A060!important;
}

/*___ Blue _________________________________________________________________________*/

ytmusic-detail-header-renderer span:nth-child(5),
#date.ytd-video-primary-info-renderer,
#metadata-line.style-scope.ytd-grid-video-renderer span:nth-child(2),
.less-button.ytd-video-secondary-info-renderer,
.metadata.ytd-notification-renderer,
.more-button.ytd-video-secondary-info-renderer,
time
 {
	color: #07beff!important;
}

/*___ Green ________________________________________________________________________*/

selector
 {
	color: #07ff24!important;
}

/*___ Red __________________________________________________________________________*/

selector
 {
	color: #ff0707!important;
}

/*__________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
	padding-left: 6px!important;
}

/*__________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f;
	opacity: 1;
}

/*___ Misc _________________________________________________________________________*/

#metadata-line.ytd-video-meta-block,
.title.ytd-guide-entry-renderer,
tp-yt-paper-item.ytd-compact-link-renderer
{
	color: #fff!important;
}

#text.ytd-channel-name {
	color: #af0!important;
}

#metadata-line.ytd-video-meta-block span:nth-child(2)
{
	color: #00b3ff!important;
}

ytd-button-renderer #button.ytd-button-renderer
{
	color: #debdcf!important;
}

.sbpqs_a {
    color: #eeddff;
}






















}

@-moz-document domain("youtube.com") {
/* Background Settings */

body,
html {
	background: url(https://image.ibb.co/hVmiOe/wallpaper_with_black_background_45_free_wallpaper.jpg)!important;
	background-attachment: fixed!important;
}

selector
{
	background-image: none;
}

.next-items-button.ytmusic-carousel,
.previous-items-button.ytmusic-carousel,
.ytp-chrome-bottom
{
	background-color: #060505a3!important;
}

/*___ Background Gray ______________________________________________________________*/

#chips-wrapper.ytd-feed-filter-chip-bar-renderer,
#guide-content.ytd-app,
ytd-carousel-header-renderer,
ytd-masthead
{
	background-color: #060505!important;
}

#left-arrow-button.ytd-feed-filter-chip-bar-renderer,
#right-arrow-button.ytd-feed-filter-chip-bar-renderer,
#right-arrow-button.yt-chip-cloud-renderer,
ytd-simple-menu-header-renderer
{
    background-color: #1a1a1a!important;
}

/*___ Background 0000 ______________________________________________________________*/

#left-arrow-button.yt-chip-cloud-renderer,
#left-arrow.yt-chip-cloud-renderer:after,
#right-arrow.yt-chip-cloud-renderer:before,
#right-arrow.ytd-feed-filter-chip-bar-renderer:before,
.sbdd_b,
.sbsb_a,
.sbsb_c,
ytd-app,
ytd-browse[page-subtype=channels]
{
	background: rgba(0, 0, 0, 0)!important;
}

/*___ Background-color 0000 ________________________________________________________*/

#container.ytd-searchbox,
#search-icon-legacy.ytd-searchbox,
yt-chip-cloud-renderer,
ytd-browse[page-subtype=playlist],
ytd-mini-guide-entry-renderer
{
	background-color: rgba(0, 0, 0, 0)!important;
}

/*__ Blue __________________________________________________________________________*/

yt-button-renderer.watch-button.ytmusic-menu-renderer,
ytcp-button[raised]
{
	background-image: linear-gradient(to bottom right, #043dda, #000)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

yt-button-renderer.watch-button.ytmusic-menu-renderer:hover,
ytcp-button[raised]:hover
{
	background-image: linear-gradient(to bottom right, #154eeb, #000)!important;
	color: #ff0!important;
}

yt-button-renderer.watch-button.ytmusic-menu-renderer:active,
ytcp-button[raised]:active
{
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Red __________________________________________________________________________*/

.style-scope.ytd-button-renderer.style-white-with-border,
tp-yt-paper-button.ytd-subscribe-button-renderer
{
	background-image: linear-gradient(to bottom right, #f00, #000)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.style-scope.ytd-button-renderer.style-white-with-border:hover,
tp-yt-paper-button.ytd-subscribe-button-renderer:hover
{
	background-image: linear-gradient(to bottom right, #f11, #000)!important;
	color: #ff0!important;
}

.style-scope.ytd-button-renderer.style-white-with-border:active,
tp-yt-paper-button.ytd-subscribe-button-renderer:active
{
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Orange ________________________________________________________________________*/

selector
{
	background-image: linear-gradient(to bottom right, #da3a04, #000)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

selector:hover
{
	background-image: linear-gradient(to bottom right, #eb4b15, #000)!important;
	color: #ff0!important;
}

selector:active
{
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*___ Green ________________________________________________________________________*/

selector
{
	background-image: linear-gradient(to bottom right, #81BC2E, #000)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

selector:hover
{
	background-image: linear-gradient(to bottom right, #92cd3f, #000)!important;
	color: #ff0!important;
}

selector:active
{
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*___ Plain Black __________________________________________________________________*/

#contents.style-scope.ytmusic-section-list-renderer,
#contents.ytd-rich-grid-renderer,
#primary.ytd-watch-flexy,
ytd-browse[page-subtype=playlist] ytd-two-column-browse-results-renderer.ytd-browse,
ytd-mini-guide-renderer,
ytd-search,
ytd-two-column-browse-results-renderer.grid:not(.grid-disabled),
ytd-watch-flexy[flexy] #secondary.ytd-watch-flexy,
ytmusic-carousel-shelf-renderer
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

#contents.style-scope.ytmusic-section-list-renderer:hover,
#contents.ytd-rich-grid-renderer:hover,
#primary.ytd-watch-flexy:hover,
ytd-browse[page-subtype=playlist] ytd-two-column-browse-results-renderer.ytd-browse:hover,
ytd-mini-guide-renderer:hover,
ytd-search:hover,
ytd-two-column-browse-results-renderer.grid:not(.grid-disabled):hover,
ytd-watch-flexy[flexy] #secondary.ytd-watch-flexy:hover,
ytmusic-carousel-shelf-renderer:hover
{
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*___ Plain ________________________________________________________________________*/

ytmusic-responsive-list-item-renderer,
.sbdd_a,
tp-yt-paper-dialog,
ytd-comment-thread-renderer,
ytd-destination-button-renderer,
ytd-grid-video-renderer,
ytd-multi-page-menu-renderer,
ytd-playlist-sidebar-renderer,
ytd-playlist-video-renderer,
ytd-two-column-browse-results-renderer[page-subtype=history] #secondary.ytd-two-column-browse-results-renderer,
ytd-video-renderer,
ytmusic-menu-popup-renderer
{
	background-color: #1c1c1c!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

ytmusic-responsive-list-item-renderer:hover,
.sbdd_a:hover,
tp-yt-paper-dialog:hover,
ytd-comment-thread-renderer:hover,
ytd-destination-button-renderer:hover,
ytd-grid-video-renderer:hover,
ytd-multi-page-menu-renderer:hover,
ytd-playlist-sidebar-renderer:hover,
ytd-playlist-video-renderer:hover,
ytd-two-column-browse-results-renderer[page-subtype=history] #secondary.ytd-two-column-browse-results-renderer:hover,
ytd-video-renderer:hover,
ytmusic-menu-popup-renderer:hover
{
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*___ Red Gradient _________________________________________________________________*/

selector
{
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

/*__ Blue Gradient _________________________________________________________________*/

yt-tooltip-renderer
{
	background: linear-gradient(to bottom, #03193c, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

/*__ Green Gradient ________________________________________________________________*/

selector
{
	background: linear-gradient(to bottom, #15534d 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	text-shadow: 1px 1px #555!important;
}

/*__ Yellow Gradient _______________________________________________________________*/

selector
{
	background: linear-gradient(to bottom, #9d9d5d 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	text-shadow: 1px 1px #555!important;
}

/*__ Orange Gradient _______________________________________________________________*/

selector
{
	background: linear-gradient(to bottom, #da3a04 0%, #000 100%)!important;
