css:
@-moz-document url(https://app.simplenote.com/) {
.simplenote-app, input[type="button"] {
    filter:grayscale(100%);
}
.app-layout__source-column {
    width: 10em;
	transition: all .2s ease-in-out;
}
.app-layout__source-column:hover {
    width: 24em;
    background: white;
}
.notes {
    overflow-x: hidden;
}
.note {
    left: 10em;
}
.note #txtarea, .note #historytxt {
    padding-left: 30px;
}
.tagbar {
    left: 6em;
}
.footer,.cookiebanner {
    display:none;
}
}
