css:
@-moz-document domain(www.zhihu.com) {
	html, body {
		font-size: /*[[global_font_size]]*/;
	}

	.Button,
	.Tag,
	.AuthorInfo-badge,
	.NumberBoard-itemValue,
	.NumberBoard-itemName,
	.AnswerItem-extraInfo,
	.ContentItem-time,
	.QuestionRichTextm,
	.QuestionMainAction,
	.QuestionRichText,
	.CommentItem,
	.CommentItem-time,
	.CommentList-dividerText
	{
		font-size: 1rem !important;
		line-height: normal !important;
	}

	.Question-sideColumn {
		display: none;
	}

	.Question-mainColumn {
		width: auto;
	}

	.Question-main, .QuestionHeader-content, .QuestionHeader-footer-inner, .AppHeader-inner {
		margin-left: 0.3rem;
		margin-right: 0.3rem;
		padding: 0;
	}

	.Button {
		padding: 0.5rem;
	}

	.QuestionHeader-title {
		padding-top: 1rem;
		padding-bottom: 0.4rem;
		font-size: 1.5rem;
		line-height: 1.8rem;
	}
		
	.Tag {
		padding: 0.5rem;
		height: auto;
	}
		
	.QuestionButtonGroup {
		display: none;
	}
	.AppHeader-inner{ 
		display: none;
	}
	.AuthorInfo-head, .CommentTopbar-title {
		font-size: 1.3rem;
	}

	.QuestionHeader-detail {
		min-height: inherit !important;
	}

	.QuestionRichText {
		max-height: inherit !important;
	}

	.List-header {
		padding-top: 0.6rem;
		padding-bottom: 0.6rem;
		height: auto;
	}

	.QuestionHeader-side {
		text-align: left;
		padding-left: 20px;
		display: none;
	}

	.QuestionHeader-main {
		width: auto;
	}

	/*.Question-main{
		width: auto;
	}
	*/
}
