css:
@-moz-document regexp("^https?://www.bilibili.com/video/av\\d(.*)"), regexp("^https?://bangumi.bilibili.com/(.*)/play#(.*)"), regexp("^https?://www.bilibili.com/watchlater/#/av(.*)"), regexp("^https?://www.bilibili.com/bangumi/play/(.*)") {
/* 列表样式 */
.list-item,
.comment-send,
#v_upinfo,
#v_tag,
#v_desc,
#arc_toolbar_report,
.video-data,
.inside-wrp,#viewbox_report>h1{
    transition: 0.3s all;
    background-color: rgba(255, 255, 255, 0.85);
    border-radius: 10px;
    padding: 10px;
}

#v_desc{
	margin-top: 10px;
}

#arc_toolbar_report {
    margin-top: 20px !important;
    border-top-left-radius: 0;
    border-top-right-radius: 0;
}
/*UP主信息*/
#v_upinfo{
	border-bottom-left-radius: 0;
   border-bottom-right-radius: 0;
	margin-bottom:0;
}

.bilibili-player-video-hint{
	height:24px;
}
.bilibili-player-video-hint span{
	margin-top:-3px
}
/* 悬浮样式 */
.list-item:hover,
.comment-send:hover,
#v_upinfo:hover,
#v_tag:hover,
#v_desc:hover,
.video-page-card:hover,
#arc_toolbar_report:hover,
.video-data:hover,
.inside-wrp:hover,
.tabs-order:hover,
.header-page:hover {
    background-color: rgba(255, 255, 255, 1);
    box-shadow: rgb(221, 221, 221) 0px 0px 10px 1px !important;
}

/* 评论发送框 */
.comment-send {
    border-radius: 0px !important;
    margin-top: 0px !important;
}

/* 评论区 */
.reply-wrap{
	border-radius:0px;
}

/* 热评最下面，圆角 */
.reply-wrap:last-child{
	border-bottom-left-radius:10px!important;
	border-bottom-right-radius:10px!important;
}


.hot-line>span {
    border-radius: 10px;
}

/*弹幕库*/
#danmukuBox{
	border-bottom-left-radius:10px!important;
	border-bottom-right-radius:10px!important;
}

/*评论和回复 */
.con {
    border-top: 0 !important;
	  border-bottom: 0 !important;
}

.con>.text,.con .text-con {
   width:95%;
}


.user-face,
.pendant {
    padding: 10px;
}

.comment-list {
    padding-top: 0 !important;
}

.comment-header,
.bb-comment,
.comment-send {
    margin: 0 !important;
    border-bottom: 0 !important;
}

#v_tag {
    border-bottom: 0;
    padding: 0px 10px;
}

.clearfix>li {
    margin: 0 5px !important;
}

/* 热评线*/
.bb-comment .hot-line{
	margin:0px;
	border-bottom:1px solid #23ade5
}

/* 评论区上部分翻页，评论框上部分排序字样，相关推荐视频字样*/ 
.bb-comment .comment-header .tabs-order,.header-page,.rec-title {
    transition: 0.3s all;
    background-color: rgba(255, 255, 255, 0.85);
    border-bottom: 0;
    border-top-left-radius: 10px !important;
    border-top-right-radius: 10px !important;
    margin-bottom: 0px;
	  padding:5px
}

/* 评论区上部分翻页*/ 
.header-page{
	margin-top:1px
}
.header-page>.result,
.header-page>.next,
.header-page>.prev {
    padding: 6px;
}
/* 右侧弹幕列表四个字背景*/ 
.player-auxiliary-area .player-auxiliary-filter{
	background-color:#fff;
}

/* 右侧推荐视频列表四个字背景*/ 
.rec-list{
	margin-top:-6px;
}

/* 右侧推荐视频列表*/ 
.video-page-card{
	transition: 0.3s all;
	background-color: rgba(255, 255, 255, 0.85);
	padding:10px 8px;
}
.video-page-card:last-child{
	border-bottom-left-radius:10px;
	border-bottom-right-radius:10px;
}

/* 大家围观的直播 关闭*/ 
#live_recommand_report{
	display:none;
}

.tag-area {
    padding: 10px 0;
}

.share {
    margin-left: 0 !important;
}



.pic>a>img {
    border-radius: 10px;
}

.pic>a>img:hover {
    border-radius: 0px;
}

.bui-collapse-header {
    background-color: rgba(255, 255, 255, 0.85) !important;
}

.bui-collapse-header:hover {
    background-color: rgba(255, 255, 255, 1) !important;
}

#live_recommand_report {
    margin-top: 25px !important;
}


#viewbox_report>h1 {
	margin:0px;
    background-color: #fff;
    padding: 10px 10px;
	border-bottom-left-radius:0;
	border-bottom-right-radius:0;
}

#bilibiliPlayer {
    box-shadow: 0 0 0;
}

/* #1890ff */
/*分类 上传时间*/
.video-data{
	border-radius:0!important;
	margin:0!important;
	float:left;
	text-align:center;
	width:549px
}
/*头像框与旁边文字*/
.up-info .u-face .pendant{
	left:-24px;
	top:-24px
}
.u-info{
	margin-top:-40px;
	margin-left:50px!important;
}

/*为我充电 关注*/
.followed,.followe{
	margin-left:0!important;
}

/*个人认证*/
.bb-comment .legalize{
	top: 45px;
    left: 50px;
}

/*播放器内部样式*/
.bilibili-player-video-hint{
	display:none!important;
}
.bilibili-player-upinfo-span .share{
	margin-left: 30px!important;
}
}

@-moz-document domain("t.bilibili.com") {

}
