@-moz-document domain("ytmommadrama.com") {

/*[[Font]]*/

/*[[SF Pro Text]]*/
/*[[IBM Plex Sans]]*/
/*[[Raleway]]*/


html, body, #wrap, .panel {
    background: #ededed;
}

::selection {
    background: palegreen;
}

.content, body, input, * {
    transition: .4s ease;
    /*background-image: none!importan;*/
}
div#page-body {
	max-width: 830px;
	margin-left: auto;
	margin-right: auto;
	min-width: 0;
    padding-left: 15px;
    padding-right: 15px;
}

/*hide ads*/
.post.bg3, #wrap > div:nth-child(4), hr, .back2top  {
    display: none;
}
span.corners-top, span.corners-bottom, span.corners-top span, span.corners-bottom span {
    opacity: 0;
}
/*thread title at top of page*/
div#page-body h2 a {
	font-weight: 800;
	letter-spacing: -.02e;
    font-size: 22px;
}

/*posts*/
.postbody {
    width: 85%;
}
/*thread title and post actions at top of each post*/
.postbody h3 {
    padding: 0;
    margin: 0!important;
}
.postbody h3 a {
    display: none;
    /*fine because clicking little paper icon to left of posters name is a post link too*/
}
/*post actions at top right of each post (report, quote, thank)*/
.postbody ul.profile-icons {
    opacity: .3;
    zoom: .8;
    margin-right: 4px;
}
.postbody ul.profile-icons:hover {
    opacity: 1;
}
/*whole entity of post */
.postbody {
    width: 100%;
}
/*author line with poster and timestamp above post body*/
p.author {
    text-transform: uppercase;
    max-width: 240px;
	background: white;
	margin-bottom: 0;
	border-top-right-radius: 10px;
	border-top-left-radius: 10px;
	padding-left:10px;
	padding-top: 5px;
}
/*postprofile*/
.postprofile {
    min-height: 0;
    border: none;
    width: 15%;
    position: absolute;
    opacity: 0;
    background: white;
	margin-left: -9px;
	margin-top: 24px;
	padding-top: 18px;
	padding-bottom: 11px;
    padding-left: 10px;
	zoom: .9;
	max-width: 150px;
	border-radius: 0px 10px 10px 10px;
}
.postprofile:hover {
    opacity: 1;
    transition: 2s fade!important;
}

/*post text itself*/
.postbody .content {
    font-size: 16px;
    background: white;
    padding: 10px;
    border-radius: 10px;
    border-top-left-radius: 0px;
    min-height: 0;
}
blockquote {
	margin-left: 0;
}
/*post thanks*/
div > div.postbody > div:nth-child(6), div > div.postbody > div:nth-child(7) {
	background: transparent;
	font-size: 12px;
    opacity: .7;
    min-height: 0;
    padding-bottom: 0;
    padding-top: 5px;
}
div > div.postbody > div:nth-child(6) dt, div > div.postbody > div:nth-child(7) dt {
	float: left;
	margin-right: 4px;
}

/*hide repeated thread title in every post*/
div.postbody h3 a {
    font-size: 10px;
}

.bg2, .bg1 {
    background: inherit;
    min-width: 0;
    margin-top: 20px;
    padding: 0
}





/**********************************************
not to use - for alternating posts

.bg2 .postprofile {
    float: left;
}
.bg2 .postbody {
    float: right;
**********************************************/


/*experimenting with background colors
***************************************/

/*this seems to be tables, seen on the portal and index page*/ .forabg {
	background-color: maroon!important;
	background-image: none;
    margin-bottom: 20px;
}

.forumbg /*seen as background of thread list tables, active topics and elsewhere*/{
    background-image: none;
    background: white;
    border: 5px solid maroon;
}
.forumbg li.header dt, li.header dd
/*table headings*/{
    background: maroon;
}


/*topiclists*/
li.row, ul.topiclist dd {
    border: none;
}





/*this section is to make the headerbar sticky*/

#page-body {
    margin-top: 115px;
}

a#logo {
	opacity: 0;
    position:fixed;
    height: 22px;
}

.headerbar {
	background-image: none;
	margin-bottom: 0;
	padding: 0px 10px;
	height: auto;
}

ul.navlinks {
	border: none;
	margin: 0;
}

div#wrap {
	padding: 0;
}

body#phpbb {
	padding-top: 0;
}

#page-header {
	position: fixed;
	width: 100%;
	z-index: 100;
}
#page-header > div.headerbar > div > span.corners-top > span, 
#page-header > div.headerbar > div > span.corners-top, 
#page-header > div.navbar > div > span.corners-top {
    display: none;
}

#site-description h1 {
	margin-top: 0;
    text-transform: lowercase;
    /*if display font...
    font-size: 23px;
    font-weight: 400;
    font-family: fredericka the great, germania one, nova round, monoton, monofett !important;*/
    
    /*if sans-serif 900 weight*/
    font-family: raleway, poppins, overpass !important;
    font-weight: 900;
    
}

div#search-box {
	margin-top: 0;
}

.headerbar .inner {
	height: auto;
}

.headerbar .inner span.corners-bottom span {
	/* display: none; */
}
#site-description {
    width: auto;
    
}
#site-description p {
	margin-bottom: 0;
	height: auto;
    display: none;
}

/*search at top right of navbar*/
/*make fieldset wide enough for searchbar and "advanced search" to float side by side*/
#search fieldset {
    min-width: 250px;
}
#search > fieldset > a {
	float: right;
    margin-top: -6px;
}
input#keywords {
	float: left;
    margin-top: 4px;
    border-radius: 3pxx;
    width: 51%!important;
    padding: 3px;
    background: rgba(255, 255, 255, .55)!important;
    border: 0px solid rgba(255, 255, 255, .55)!important;
}
input#keywords:focus {
    background: white!important;
}

/*hide search button*/
#search > fieldset > input.button2 {
	display: none;
}

/**viewport size - hide some header junk so it doesn't spill onto two lines when screen is narrower than 1050px**/
@media only screen and (max-width: 1049px) {

       li.icon-news, li.icon-rules, li.icon-faq { display:none!important;}
    li.icon-logout {  max-height: 25px;}

    }

  
