/* ==UserStyle==
@name           LichessTV Zen Mode
@namespace      github.com/ddugovic/UserStyles
@version        1.1.0
@description    A more relaxed LichessTV experience.
@author         Daniel Dugovic
==/UserStyle== */
@-moz-document url-prefix("https://lichess.org/tv"), url-prefix("https://lidraughts.org/tv") {
body.zen #top,
body.zen .tv-channels {
  opacity: 0;
  transition: 1s;
}
body.zen #top:hover,
body.zen.offline #reconnecting,
body.zen .tv-channels:hover {
  opacity: 1 !important;
}
}
