::-moz-scrollbar-track{background-color:var(--bg-2) !important}
::-moz-scrollbar-button{display:none !important}
::-moz-scrollbar-thumb{background-color:var(--fg-link) !important;border-radius:4px !important;border:none !important }
::-moz-scrollbar-thumb:hover{background-color:var(--fg-link-hover) !important}
::-webkit-scrollbar{background-color:var(--bg-2) !important;width:10px !important}
::-webkit-scrollbar-track{background-color:var(--bg-2) !important}
::-webkit-scrollbar-button{display:none !important}
::-webkit-scrollbar-thumb{background-color:var(--fg-link) !important;border-radius:4px !important;border:none !important }
::-webkit-scrollbar-thumb:hover{background-color:var(--fg-link-hover) !important}

