.listo ul.list_tabs li.active a, .trailer-content li.active.trailer-tab a, .trailer-content li.active.trailer-tab a:visited, .trailer-content li.active.trailer-tab a:hover, .trailer-content li.active.trailer-tab a:focus, #nb_search .primary.btn, .faceter > .faceter-header > .facets-active > .facet, .plugins .legalMsg {
