css:
@-moz-document domain("cubetimer.com") {
#scramble {
    background: transparent;
    border: none;
    color: /*[[color]]*/ !important;
}
td[style="border: 1px solid #89c; background: #BACAFE url('stats_bg.jpg') repeat-x; width: 250px;"] {
    background: /*[[bgcolor]]*/ !important;
    border: none !important;
    color: /*[[color]]*/ !important;
}
#times, #times div, #stats, #stats table tbody tr td label {
    background: /*[[stcolor]]*/ !important;
    color: /*[[color]]*/ !important;
    border: none !important;
}
#times div {
    margin-bottom: 0 !important;
    padding-bottom: 10px !important;
}
select {
    background-color: /*[[stcolor]]*/;
    border: none;
    border-radius: 5px;
    color: /*[[color]]*/;
}
#puzzle_selector select {margin-top: 4px;}
table tbody tr td {vertical-align: middle;}
#stats {border-radius: 0px 0px 10px 10px;}
body {background: /*[[bgcolor]]*/ !important;}
div[style="height: 135px; overflow: auto; padding: 5px; margin: 10px; border: 1px solid #89c; background: #fff;"] {overflow: hidden !important;}
#times {border-radius: 10px 10px 0px 0px;}
#elapsed_time, a {color: /*[[color]]*/ !important;}
table {border-bottom: 0 !important;}
input[type="checkbox"], label[for="insp_sound"], h1[style="height: 106px; width: 416px; margin: 0 auto;"], div[style="width: 1000px; margin: 0 auto; text-align: left;"], #short_info, #avg {display: none;}
}
