css:
@-moz-document domain("outlook.live.com")
{
 div[id="app"] > div:nth-child(1) > div:nth-child(3) > div:nth-child(1) > div:nth-child(1) > div:nth-child(4) {
    display: none !important;
}


div[id="app"] > div:nth-child(1) > div:nth-child(3) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(2) > div:nth-child(1) > div:nth-child(2) {
    display: none !important;
}

@media only screen and (max-width: 990px) {
    div[id="app"] > div:nth-child(1) > div:nth-child(3) > div:nth-child(1) > div:nth-child(2) {
        display: none !important;
    }
}
}
