css:
@-moz-document domain('hitek.fr') {
/* Body */
body {
    background: rgb(15, 15, 15);
    color: #aaa;
}

.newsletter input[type='text'],
.price-newsletter input[type='text'] {
    border-color: #666;
    background-color: transparent;
    color: #fff;
}

/* Header */
.header-container,
.header-container2 {
    background: rgb(25, 25, 25);
}

.header-container .logo img,
.header-container2 .logo2 img {
    -webkit-filter: brightness(500%) grayscale(100%);
    -moz-filter: brightness(500%) grayscale(100%);
    -ms-filter: brightness(500%) grayscale(100%);
    -o-filter: brightness(500%) grayscale(100%);
    filter: brightness(500%) grayscale(100%);
}

.header-basics {
    border-color: transparent;
}

.search,
.search2 {
    border-color: #666;
    background-color: transparent;
    color: #fff;
}

.header-menu li {
    border-color: #444;
}

.header-menu a {
    color: #cbcbcb;
}
.actu-link a {
    color: #fff;
}

.dropdown-menu .dropped-menu,
.dropped-container,
.product-pic {
    background: rgb(35, 35, 35);
}

/* Side bar */
.follow,
#popular_articles {
    background: rgb(35, 35, 35);
}
.content-basics {
    border-color: rgb(55, 55, 55);
}

/* Page */
.content-container {
    background: rgb(25, 25, 25);
}

.content-articles p,
.articles-like .social-bar .reactions .label,
.articles-like .social-bar .reactions.empty span,
.tag,
.source {
    color: #aaa;
}

blockquote {
    background: rgb(35, 35, 35);
    border-color: rgb(45, 45, 45);
}

.sticker-hidden {
    display: none;
}
.articles-like {
    background: rgb(35, 35, 35);
    border-color: rgb(45, 45, 45);
    position: static !important;
}
.tag,
.source,
.pagin-article div.left,
.pagin-article div.right {
    background-color: rgb(35, 35, 35);
}

.pagin-article img {
    -webkit-filter: brightness(500%) grayscale(100%);
    -moz-filter: brightness(500%) grayscale(100%);
    -ms-filter: brightness(500%) grayscale(100%);
    -o-filter: brightness(500%) grayscale(100%);
    filter: brightness(500%) grayscale(100%);
}

.pagin-article div.left:hover,
.pagin-article div.right:hover {
    background-color: rgb(15, 15, 15);
}
.about {background: rgba(255,255,255,.05);}

/* Barre des réactions et partage */
.sharebar {
    background: rgba(255,255,255,.1);
    border: none;
}

/* Onglets */
.menu li {
    background: rgb(35, 35, 35);
    border-color: rgb(25, 25, 25);
}

.products-preview/*, .poster*/
{
    background: #2d2d2d;
}

.product-poster .product-title {
    color: #aaa;
}



/* Message articles manqués */
.news-message {
    background: rgb(40, 40, 40);
}



/* Sous-titres des articles (date et auteur) */
.news-infos {
    color: #555;
}



/* Bouton afficher plus */
.content-footer {
    background: rgb(40, 40, 40);
}

/* Quiz */
.quiz
{
    background: rgb(40, 40, 40);
    border-radius: 5px;
}
.quiz .question .question-title 
{
    color: #fff;
    margin: 0 20px;
}
.quiz .question .answers
{
    background: transparent;
}
.quiz .question .answer
{
    background: rgb(50,50,50);
    border-color: rgb(70,70,70); 
}
.quiz .question .answer:hover
{
    color: #fff;
    cursor: pointer;
    background: rgb(70,70,70);
    border-color: rgb(150,150,150); 
}

.quiz .question .answer.selected
{
    background: rgba(255,255,255,.2) !important;
}
.quiz .question .next-question
{
    background: rgb(35,35,35);
    padding: 5px 20px;
    padding-top: 7px;
}
.scores-bar,
.scores-bar:hover
{
    background: rgb(50,50,50);
    border-color: transparent;
}
.background-bar-sondage
{
    background: rgba(255,255,255,.1);
}

/* Boutons de pagination */
.articles_page.active {
    background: #a22728;
}
.articles_page {
    background: rgb(55, 55, 55);
}

/* Commentaires */
.input-text,
.input-area {
    background: rgb(35, 35, 35);
    border-color: rgb(45, 45, 45);
    color: #aaa;
}

/* Footer */
.footer-container {
    background: rgb(25, 25, 25);
}

