css:
@-moz-document url-prefix(https://dtf.ru/){

@media all and (min-width: 1200px) {
    
body, .main_menu 
{ padding: 0 !important; }

.sticky_sidebar, .live-wrapper, .comments_updates, .island--vacancies_entry, .club_widgets, .feed__chunk .feed__item .entry_footer, .feed__chunk  .island--vacancies_recent, .feed__chunk .feed__item blockquote, div[data-name="vacancies"] 
{ display: none !important; }

.content.l-width-640 
{ width: 100%; }

.page--index .feed 
{ overflow: hidden; }

.page--index .feed__chunk .feed__item 
{ position: relative; padding: 15px 25px 25px 25px; background: #fff; box-shadow: 0 1px 1px rgba(0,0,0,.1); margin-bottom: 9px; }

.page--index .feed__chunk .feed__item .entry_header 
{ position: absolute; top: 15px; right: 0; width: 59%; }

.page--index .feed__chunk .feed__item .b-article 
{ overflow: hidden; }

.page--index .feed__chunk .feed__item figure 
{ width: 40%; margin: 20px 0 0 0 !important; }

.page--index .feed__chunk .feed__item h2 
{ margin-top: 50px; }

.page--index .feed__chunk .feed__item h2, .page--index .feed__chunk .feed__item p 
{ float: right; width: 60%; }

.booster_unit, .subsite_promo, .vacancies_widget, .booster-unit, .booster-unit--placeholder 
{ display: none !important; }

}
}
