css:
@-moz-document domain("tildes.net") {
/*[[theme]]*/

html, body {
  background-color: var(--bg) !important }
body main, main, #page-list, body #sidebar, footer {
  background-color: var(--bg-2) !important }
div.toc, body .topic, body tbody tr:nth-of-type(2n+1), body .topic-listing > li:nth-of-type(2n) {
  background-color: var(--bg-odd) !important }
.empty, .btn:focus, .btn:hover, body .comment header, body .is-comment-collapsed:not(:target) header, body .message header, body .topic, body .topic footer, footer#site-footer {
  background-color: #0000 !important }
body blockquote {
  background-color: #fff1 !important }

asdf, .table th, body .message {
  border-color: var(--border-color) !important }
div.toc .toctitle, code, hr, .table td {
  border-color: #fff3 !important }
body .comment {
  border-color: #fff8 !important }
h1, h2, h3 {
  border-color: var(--fg-2) !important }
.topic-icon, body .tab-listing-order, .tab, body .comment[data-comment-depth="0"], body section {
  border-color: var(--fg-link) !important }
.is-comment-by-op > .comment-itself, .is-topic-official {
  border-color: var(--fg-link) !important }
.tab .tab-item.active a {
  border-color: var(--fg-link-hover) !important }

html, body, body .topic-text-excerpt, body .topic-text-excerpt[open] {
  color: var(--fg) !important }
h1, h2, h3, body .comment header, a.link-group, a.link-group:visited, body .site-header-context {
  color: var(--fg-2) !important }
.date-info, .date-info a, .date-info a:link, body .label-topic-tag a, body .topic-info, .time-responsive-full, body .label-topic-tag, .label, body .message header, body .topic-content-metadata, footer#site-footer, body .text-secondary {
  color: var(--fg-muted) !important }

a, a:link, header a, header a:visited, footer a, footer a:visited, .btn, .nav .nav-item a, .nav .nav-item a:link, .btn.btn-link, body .topic-text-excerpt summary::after {
  color: var(--fg-link) !important }
a:hover, footer a:hover, header a:hover, .date-info a:hover, .btn:focus, .btn:hover, .nav .nav-item a:hover, .btn.btn-link:hover, .tab .tab-item.active a, .tab .tab-item a:hover, .tab .tab-item a:focus, body .post-button:hover, body .label-topic-tag a:hover, .nav .nav li.active a, .nav .nav-item.active > a:focus,.nav .nav-item.active > a:hover {
  color: var(--fg-link-hover) !important }
a:visited, .nav .nav-item a:visited {
  color: var(--fg-link-visited) !important }

code, body code, body pre {
  background-color: var(--syntax-bg) !important }
code, body code, body pre {
  color: var(--syntax-fg) !important }

/* Input */
body .form-input, .form-input, .form-checkbox .form-icon, .form-radio .form-icon, body .form-select:not([multiple]):not([size]) {
  background-color: var(--syntax-bg) !important;
  color: var(--syntax-fg) !important;
  border: solid 0.5px var(--fg-2) !important }
/* Input borders */
body .form-input:focus, .btn {
  border: solid 0.5px var(--fg-link) !important }
body .form-input:not(:focus), .btn:focus, .btn:hover {
  border: solid 0.5px var(--fg-link-hover) !important }
/* Buttons & checkboxes */
.form-checkbox input:checked + .form-icon, .btn.btn-primary {
  background-color: var(--fg-link) !important }
.btn.btn-primary:focus, .btn.btn-primary:hover {
  background-color: var(--fg-link-hover) !important }
.form-checkbox input:checked + .form-icon::before {
  border-color: var(--bg) !important }
.btn.btn-primary, .btn.btn-primary:focus, .btn.btn-primary:hover {
  color: var(--bg) !important }

/* Labels */
body .label-topic-tag-spoiler { background-color: #ff0 !important }
body .label-topic-tag-spoiler a { color: #000 !important }

label[for="theme"]:after {
  color: #f33;
  content: " NOTE: Theme is overridden by Stylus!" }

}
