css:
@-moz-document url-prefix("https://flow-e.com/workflow") {
@media (max-width:1500px) {
    /* hide timeline */
    div.timeline { height: 0; }
    div.flowe-container { height: calc(100% - 10px); }

    /* collapse header until hover
    div.header { max-height: 10px; transition: max-height .5s; }
    div.header * { opacity: 0; transition: opacity .5s; }
    div.header:hover { max-height: 50px; transition: max-height .5s; }
    div.header:hover * { opacity: 1; transition: opacity .5s; }
    /*

    /* narrower columns */
    div.column:not(.is-collapsed), div.feed:not(.is-collapsed) { width: 280px; min-width: 280px; }
}
}
