css:
@-moz-document domain("www.playground.ru") {
.page-main-column,
.btn-default,
.content-rating-area
,body
{
    background-color: #373737;  /* Основной цвет фона */
}

.content-comments-header,
.textareaControlls .control,
.comments-posting-form textarea
,.comments-content .inner blockquote
,.story-content
{
    background: #2f2f2f;    /* Темнее фона */
}


.article-content,
.article-title,
body,
.btn-default,
.media-announce-text,
.module-title,
.content-comments-header
,.comments-posting-form textarea
{
    color: #b0b0b0;
}

.gp-game-title a,
a,
.tileview-list-item .tileview-item-header a,
.media-superscript a,
.article-related-content a
{
    color: #63b867;     /* Ссылки */
}


.article-metadata
,.comment-row
,hr
,.btn-default
,.textareaControlls .control
,.comments-posting-form textarea
,.comments-content .inner blockquote
{
    border-color: #5f5f5f;  /* Цвет границы */
}

.tileview-list-item .cover-image
,.article-theme-image
,.content-comments-header
,.article-related-content img
,.story-image
{
    border-radius: 8px;     /* Закругленные углы */
}


:root
{
    --main-color: #63b867; 
}

}
