@-moz-document url("https://twitter.com/"), url("https://twitter.com/?logged_out=1&lang=ru") {
.dashboard-left {
    position: fixed!important;
}
.content-main {
    margin-left: 300px!important;
}
.dashboard-right {
    position: fixed;
    margin-left: 900px!important;
}
.is-square {
    max-height: 100%!important;
}
.AdaptiveMedia-photoContainer img {
    top: 0!important;
}
.AdaptiveMedia {
    max-height: 100%;
}
.AdaptiveMedia-doublePhoto {
    height: 379px;
}
.AdaptiveMedia-triplePhoto {
    height: 505px;
}
.AdaptiveMedia-quadPhoto {
    height: 569px;
}
}
