css:
@-moz-document domain("scratch.mit.edu") {
#topnav .innerwrap {
    background-color: #0f8bc03b
}
html, body {
    background: url(/*[[background]]*/) no-repeat center center fixed;
        -webkit-background-size: cover;
  -moz-background-size: cover;
  -o-background-size: cover;
  background-size: cover;
}
#topnav ul.account-nav .logged-in-user .dropdown-menu {
    background-color: #0f8bc08c
}
#topnav li.logout.divider input {
    background-color: transparent
}
#topnav ul.account-nav .logged-in-user .user-icon {
    border-radius: 100px;
}
#comments .comment img.avatar {
     border-radius: 100px;
}
#topnav .logo span {
    background: url(https://scratch.mit.edu/images/logo_sm.png) no-repeat left center;
    background-size: 60%;
    transition: .15s ease all;
}
.box {
    background-color: #0f8bc06e;
    border: grey;
}
.button, button {
    background-color: #0f8bc04f;
}

.box .box-head h4 {
    color: white;
    text-shadow: none;
}

.slider-carousel .project.thumb img {
    border-radius: 20px;
}
#footer {
    background-color: #0f8bc04f;
    color: white;
}
#footer ul.footer-col li h4 {
    color: white;
}
#footer ul.footer-col a {
    color: white;
}
.box .box-head .header-text h2 {
    color: white;
    text-shadow: none;
}
.header-text .profile-details {
    color: white;
}
.slider-carousel .thumb img {
     border-radius: 100px;
}
.box .box-head .portrait img {
    border-radius: 100px;
}
#comments #comment-form .avatar {
    border-radius: 100px;
}
#comments #comment-form textarea {
    border-radius: 100px;
}
.iframeshim {
    display: none;
}
}
