css:
@-moz-document domain("c2.com") {
html {
    background: #ccc;
}

#page {
    max-width: /*[[max_width]]*/;
    text-align: justify;
    font-family: /*[[font]]*/;
    margin-left: auto;
    margin-right: auto;
    margin-top: 0px;
    margin-bottom: 0px;
    padding: 50px;
    font-size: 13pt;
    box-shadow: 0px 0px 200px 200px #8080807F;
    border-radius: 4px;
    line-height: 140%;
    background: #FFF;
}

a:visited {
    color: /*[[accent_color]]*/;
}

a {
    color: /*[[accent_color]]*/;
    text-decoration: none;
}

a:hover {
    text-decoration: underline;
}

h1, h2, h3, h4, h5, h6 {
    font-family: /*[[font]]*/;
}
}
