css:
@-moz-document regexp("https:\/\/.*\.syncromsp\.com\/.*") {
/* Main navigation bar */
.subnavbar .subnavbar-inner {
    background: #0D48A1!important;
}
body.rs2.dark .subnavbar .subnavbar-inner {
    background: #171717!important;
}
/* Main navigation bar active tab */
body.cn.dark .subnavbar .subnavbar-inner .mainnav li.active > a,
.subnavbar .subnavbar-inner .mainnav > li.active {
    background-color: #666666!important;
}
/* Main navigation bar active tab pointer */
.subnavbar .subnavbar-inner .subnav-collapse.collapse .mainnav > li.active a::after {
    border-top: 7px solid #666666!important;
}
/* Main navigation bar dropdown menu item active */
.subnavbar .subnavbar-inner .mainnav li .dropdown-menu li.active > a {
    background-color: #0D48A1!important;
}
/* Main navigation bar dropdown menu item mouseover */
.subnavbar .subnavbar-inner .mainnav li .dropdown-menu li:hover > a {
    background-color: #0D48A1!important;
}
/* Page name header text on some pages */
h1 {
    color: #666666!important;
}
/* Page name header text on some pages */
.customer-title {
    color: #666666!important;
}
/* Dashboard header text */
.cardbox .cardbox-row .dashboard-card .card-title {
    color: #666666!important;
}
/* Dashboard chart header text */
.highcharts-title {
    color: #666666!important;
}
/* Dashboard chart axis lines */
.highcharts-axis-line {
    stroke: #0D48A1;
}
/* Links */
a, .text-primary, .btn-link {
    color: #0D48A1;
}
a {
    color: #0D48A1;
}
body.cn.dark a:not(.btn):not(.badge):not(.help-icon):not(.additional-items-link):not(.navbar-brand):not(.ui-corner-all):not(.red):not(.green):not(.warning):not(.orange):not(.current):not(.chosen-single), body.cn.dark .btn-link, body.cn.dark .text-primary {
    color: #555555;
}
/* Help Button */
.u-userLauncherColor:not([disabled]) {
    background-color: #666666!important;
}
/* Buttons */
body.cn .btn-default {
    color: #0D48A1;
}
/* Buttons */
body.cn .btn-primary, body.cn .btn-teal, body.cn .btn-success {
    background-color: #0D48A1!important;
    border-color: #0D48A1!important;
}
/* Drop-down menu on top right item active */
body.cn .dropdown-menu > .active > a {
    background-color: #0D48A1;
}
/* Drop-down menu on top right item mouseover */
body.cn .dropdown-menu > li > a:hover {
    background-color: #0D48A1;
}
/* Background color of widget headers */
body.cn .widget .widget-header {
    background-image: linear-gradient(-53deg, #0D48A1 3%, #0D48A1 92%);
}
/* Plus icon dropdown menu mouseover */
body.cn .navbar.navbar-inverse .dropdown-menu li > a:hover {
    background-color: #0D48A1;
}
/* Assets & RMM tab Overview and Alerts header text */
.blue-tile-header {
    color: #666666;
}
/* Asset sidebar widget text */
body.cn .widget.borderless .widget-header i, body.cn .widget.borderless .widget-header h3 {
    color: #666666;
}
/* Asset sidebar widget dividing line */
body.cn .widget.borderless .widget-header {
    border-bottom-color: #666666;
}
/* Windows Patch Policy Category Drop Down */
body.rs2.cn li.active a label.checkbox {
 	background-color: #ffffff;
    color: #555555;
}
body.rs2.cn li a:hover label.checkbox {
 	background-color: #0D48A1;
    color: #ffffff;
}
/* Page numbers */
ul.pagination li a {
    color: #0D48A1;
}
/* Page numbers mouseover */
.pagination > li > a:hover, .pagination > li > span:hover, .pagination > li > a:focus, .pagination > li > span:focus {
    color: #0D48A1;
}
/* Bonus: Change Mailer to look like the other menu items */
 #marketr-nav:not(.active) a {
    color: #555555;
}
#marketr-nav:hover > a {
    color: #ffffff;
}
}
