css:
@-moz-document url-prefix("http://www.portalroms.com/") {
#header {background-image: -webkit-linear-gradient(top,#1274ff 0%,#1b1a1a 100%);}
#header-menu {
    background: #222;
    border-top: 1px solid #353535;
    border-bottom: 1px solid #353535;
    background-color: #6b1f1f;
}#content {background-image: -webkit-linear-gradient(top,#0b0f24 0%,#1f253d 100%);}
.region-sidebar-first .block .content, .region-sidebar-second .block .content {
    background: #1f253d;
}body {
    color: #ffffff;
    background: #1a1a1a;
}a, li a.active, #footer a:hover, #footer li a.active:hover, #footer-bottom a:hover, 
#footer-bottom li a.active:hover, #header a, #header-menu ul.menu li a:hover, #header-menu 
ul.menu li a.active, #header-menu ul.menu li ul.menu li a:hover, #navigation 
.block-superfish ul.sf-menu li a:focus, #navigation .block-superfish ul.sf-menu li a:hover, 
#navigation .block-superfish ul.sf-menu li a.active, .front .node h1.front-heading {
    color: #23ff0a;}
}
