css:
@-moz-document url-prefix("https://www.novinky.cz/") {
body, p, select, input,
#page, #footer, #articleBox, #subpage,
#sectionHead #theme, #rightColumn,
.hp #main,
.hp #TopStory,
.hp #sectionBox,
.hp #sectionBox h2,
.hp #sectionContainer,
.hp #sectionBox .item,
.hp #subsectionContainer,
.hp #subsections .subBox,
.section #sectionContainer,
.section #rightColumn,
.content .articleBody p,
.content .articleBody td,
.newContrib,
.msgBoxOut,
.contribution .lineTop div.infoDate,
.contribution .lineTop,
#sectionBox .item, .entry-content .item,
#sectionSeparatorLine,
#articleContent .topBordered,
#articleContent #info #columnInfo p, #articleContent #info #relatedTopics p,
#articleContent #info #columnInfo h3,
#articleContent .content p.acmAuthor,
.webSliceFormat, #sectionBox h2, #sectionBox h1,
.discussion #sectionContainer,
.article #navigation, .topic-hp #navigation {
    background: black;
    color: #949468 !important;
    border-color: #605e5e;
}

h1 a, h2 a, h3 a, h4 a, h5 a, h6 a,
#hintBox p a,
#articleContent #articleBody a,
#inquiries .inqItem .inqPhrase a,
#articleContent .related ul li a,
.hp #subsections .subItem .mainArticle a,
.hp #subsections .subItem li a,
.hp #subsections .sportBox li a,
.hp #hpGallery p a,
.ordering a.actualOrder,s
.newContrib a {
  color: #6262bf;
  border-color: #605e5e !important;
}

.my-news #sectionHead #navigation img,
#logo img {
    display: none;
}

#logo a {
    color: darkred;
    padding-left: 5px;
    text-decoration: none;
    font-family: sans-serif;
}

#logo a:after {
    content: "Novinky.cz";
    font-size: 32px;
}

#headForm form #searchInput, 
input[type="submit"],
.article div.separatorLine {
    background-color: #3a3737;
    border-color: #211f1f;
    color: #949468;
}

.TSSmall h2.pgSection,
#sectionHead #navigation h1 {
    background: none;
    padding-left: 0;
    color: #949468;
}


.article #articleHeaderBig p.perex {
    color: #b9160f
}
}
