css:
@-moz-document domain(klavogonki.ru) {
    #fontsize_cont {
        display: flex !important;
        justify-content: center !important;
    }
    #main-block,
    #typeblock {
        width: var(--container) !important;
    }
    #typeblock #inputtext {
        width: 100% !important;
    }
    #play-right.with-ads {
        top: 95px !important;
        right: 0px;
    }
	#play-right #invite {
		top: -60px !important;
		right: 1615px !important;
	}
    @media (max-width: 1850px) {
        html {
            overflow-x: hidden !important;
        }
        #params,
        #invite {
            display: none !important;
        }
        #play-right.with-ads {
            top: 95px !important;
            right: 0 !important;
        }
    }
}
