css:
@-moz-document domain("trrrending.today") {
.header, .post, .top-embed, .popup, .fire-indicator__tip {
    background: /*[[pc]]*/ !important;
}

.avatar__placeholder {
    background: /*[[pc]]*/;
}

.header-socnet, .header__about, .feed-socnet, .period-switcher__item, .account__name, .post-text, .n-top__link, .top-embed__ratingk, .top-embed__sharing span, .top-embed__rating, .share-block__item, .popup-text__paragraph_with-icon{
    color:/*[[tc]]*/ !important;
}

body, .copylink {
    background-color: /*[[bgc]]*/ !important;
}

.post-position-label , .top-embed__btn{
    background-color: #585858 !important;
    color: #D5CBCB !important;
}

.account__login, .post-action__info, .post-meta, .post-text-expandlink, .n-top__title, .top-embed__subscribers, .copylink-btn, .fire-indicator__tip{
    color:/*[[stc]]*/ !important
}

.post-action__icon svg {
    fill: /*[[stc]]*/ !important
}

.post-action__icon_comment svg, .post-action__icon_eye svg, .post-action__icon_star svg {
    stroke: /*[[stc]]*/ !important;
}

.period-switcher__item:hover {
    background-color: rgba(255, 255, 255, 0.16);
}

.avatar__position {
    box-shadow: 0 0 0 1px #353535;
}

.profile_disabled .account__login, .profile_disabled a {
    color:#434546 !important;
}

.top-embed__sharing svg, .popup-close-btn svg {
    fill: /*[[tc]]*/ !important;
}

.header-socnet_twitter:hover , .hoverable:hover, .copylink-btn:hover, .hoverable_twitter:hover {
    color: #1da1f2 !important;
}

.header-socnet_instagram:hover, .hoverable_instagram:hover {
    color: #b5349a !important;
}

.header-socnet_telegram:hover, .hoverable_telegram:hover {
    color: #0095c6 !important;
}

.period-switcher_twitter .period-switcher__item--is-active, .period-switcher_twitter .period-switcher__item--is-active:hover {
    color: #fff !important;
}

.period-switcher_telegram .period-switcher__item--is-active, .period-switcher_telegram .period-switcher__item--is-active:hover {
    color: #fff !important;
    background-color: #0095c6 !important;
}

.period-switcher_instagram .period-switcher__item--is-active, .period-switcher_instagram .period-switcher__item--is-active:hover {
    color: #fff !important;
    background-color: #b5349a !important;
}

.popup-text__title {
    color: #95a1ce !important;
}

.popup-close-btn:hover svg {
    fill: #1da1f2 !important;
}
.up-btn-content__item_icon svg {
    fill: rgba(80, 80, 80, 0.7);
}

.hoverable_telegram:hover .post-action__icon_comment svg, .hoverable_telegram:hover .post-action__icon_eye svg, .hoverable_telegram:hover .post-action__icon_star svg {
    stroke: #0095c6 !important;
}

.hoverable_twitter:hover .post-action__icon_like svg, .hoverable_twitter:hover .post-action__icon_repost svg, .hoverable_twitter:hover .post-action__icon_share svg, .hoverable_twitter:hover .top-embed__sharing_icon svg {
    fill: #1da1f2 !important;
}

.hoverable_instagram:hover .post-action__icon_comment svg, .hoverable_instagram:hover .post-action__icon_eye svg, .hoverable_instagram:hover .post-action__icon_star svg {
    stroke: #b5349a !important;
}

.hoverable_instagram:hover .post-action__icon_like svg {
    fill: #b5349a !important;
}
 .profile_twitter:hover .account__login {
   color: #1da1f2 !important;
}
 .profile_telegram:hover .account__login {
    color: #0095c6 !important;
}
 .profile_instagram:hover .account__login {
    color: #b5349a !important;
}
}
