css:
/* i really want this to be global */
.markdown-renderer-async,.markdown-render-area {
    direction: rtl;
    text-align:right;
    padding-left: 0!important;
    padding-right: 48px!important;
}

.markdown-renderer-async pre,.markdown-render-area pre{
direction:ltr;
text-align:left;
}

.wiki-markdown {
    direction: rtl;
}


.monaco-editor .view-line{
direction: rtl;
}

.lines-content.monaco-editor-background .mtk1{
    font-family: "Segoe UI VSS (Regular)","Segoe UI","-apple-system",BlinkMacSystemFont,Roboto,"Helvetica Neue",Helvetica,Ubuntu,Arial,sans-serif,"Apple Color Emoji","Segoe UI Emoji","Segoe UI Symbol"!important;
    font-size: 16px!important;
}
