css:
/* i really want this to be global */
header,
.cnbeta-update-list-article,
.cnbeta-home-side,
.cnbeta-side-sponsor,
footer {
    display: none !important;
}
body,
.cnbeta-home-blue-wrapper {
    background: #000;
}
.topContentInner,
.cnbeta-article-wrapper {
    background-image: none;
}
.cnbeta-update-list{width:100%;float:initial}
.cnbeta-update-list .item dl {
    padding: 20px 20px 20px 170px !important;
    background: #fff
}
.cnbeta-update-list .item:hover,
.cnbeta-update-list .item,
.cnbeta-article-body {
    background: #fff !important;
}

#ad,
iframe,
.cooperation,
[id^="taboola"] {
    opacity: 0 !important;
}
.cooperation,
[id^="taboola"] {
    height: 10px !important;
    overflow: hidden;
}
