css:
@-moz-document regexp(".*") {   
    html[hide-scrollbar="true"] ::-webkit-scrollbar {
        display: none;
    }
    ::-webkit-scrollbar {
        width: /*[[size]]*/ !important;
        height: /*[[size]]*/ !important;
    }
    ::-webkit-scrollbar-thumb {
        -webkit-appearance: none !important;
        background: /*[[bar]]*/ !important;
        border-radius: /*[[br]]*/ !important;
        box-shadow: none !important;
        width: /*[[size]]*/ !important;
        height: /*[[size]]*/ !important;
    }
    ::-webkit-scrollbar-thumb:hover {
        -webkit-appearance: none !important;
        background: /*[[hover]]*/ !important;
    }
    ::-webkit-scrollbar-thumb:active {
        -webkit-appearance: none !important;
        background: /*[[active]]*/ !important;
    }
    ::-webkit-scrollbar-thumb:vertical {
        border-left: /*[[border]]*/ solid /*[[bc]]*/ !important;
        border-right: /*[[border]]*/ solid /*[[bc]]*/ !important;
        border-top: /*[[borderp]]*/ solid /*[[bc]]*/ !important;
        border-bottom: /*[[borderp]]*/ solid /*[[bc]]*/ !important;
    }
    ::-webkit-scrollbar-thumb:horizontal {
        border-top: /*[[border]]*/ solid /*[[bc]]*/ !important;
        border-bottom: /*[[border]]*/ solid /*[[bc]]*/ !important;
        border-left: /*[[borderp]]*/ solid /*[[bc]]*/ !important;
        border-right: /*[[borderp]]*/ solid /*[[bc]]*/ !important;
    }
    ::-webkit-scrollbar-track {
        -webkit-appearance: none !important;
        background: /*[[bg]]*/ !important;
        border-radius: 0 !important;
        box-shadow: none !important;
        width: /*[[size]]*/ !important;
        height: /*[[size]]*/ !important;
    }
    ::-webkit-scrollbar-track:vertical {
        -webkit-appearance: none !important;
        border-left: /*[[border]]*/ solid /*[[bc]]*/ !important;
        border-right: /*[[border]]*/ solid /*[[bc]]*/ !important;
        border-top: /*[[borderp]]*/ solid /*[[bc]]*/ !important;
        border-bottom: /*[[borderp]]*/ solid /*[[bc]]*/ !important;
    }
    ::-webkit-scrollbar-track:horizontal {
        -webkit-appearance: none !important;
        border-top: /*[[border]]*/ solid /*[[bc]]*/ !important;
        border-bottom: /*[[border]]*/ solid /*[[bc]]*/ !important;
        border-left: /*[[borderp]]*/ solid /*[[bc]]*/ !important;
        border-right: /*[[borderp]]*/ solid /*[[bc]]*/ !important;
    }
    ::-webkit-scrollbar-track-piece {
        -webkit-appearance: none !important;
        background: transparent !important;
        border: none !important;
        box-shadow: none !important;
        width: /*[[size]]*/ !important;
        height: /*[[size]]*/ !important;
    }
    ::-webkit-scrollbar-corner {
        -webkit-appearance: none !important;
        background: /*[[corner]]*/ !important;
        border: none !important;
        border-radius: 0 !important;
        box-shadow: none !important;
    }
    ::-webkit-scrollbar-button {
        -webkit-appearance: none !important;
        width: /*[[button]]*/ !important;
        height: /*[[button]]*/ !important;
        border-radius: 0 !important;
    }
    ::-webkit-scrollbar-button:vertical {
        border-left: /*[[borderb]]*/ solid /*[[bc]]*/ !important;
        border-right: /*[[borderb]]*/ solid /*[[bc]]*/ !important;
        border-top: /*[[borderbp]]*/ solid /*[[bc]]*/ !important;
        border-bottom: /*[[borderbp]]*/ solid /*[[bc]]*/ !important;
    }
    ::-webkit-scrollbar-button:horizontal {
        border-top: /*[[border]]*/ solid /*[[bc]]*/ !important;
        border-bottom: /*[[border]]*/ solid /*[[bc]]*/ !important;
        border-left: /*[[borderp]]*/ solid /*[[bc]]*/ !important;
        border-right: /*[[borderp]]*/ solid /*[[bc]]*/ !important;
    }
    /*[[arrow]]*/
    /*[[bharrow]]*/
    /*[[baarrow]]*/
    ::-webkit-scrollbar-button:increment:start {
        width: 0 !important;
        height: 0 !important;
    }
    ::-webkit-scrollbar-button:decrement:end {
        width: 0 !important;
        height: 0 !important;
    }
    ::-webkit-scrollbar-button:vertical:decrement {
        -webkit-appearance: none !important;
        background-position: center !important;
        background-size: /*[[button]]*/ !important;
        background-color: /*[[bg]]*/ !important;
    }
    ::-webkit-scrollbar-button:vertical:increment {
        -webkit-appearance: none !important;
        background-position: center !important;
        background-size: /*[[button]]*/ !important;
        background-color: /*[[bg]]*/ !important;
    }
    ::-webkit-scrollbar-button:horizontal:decrement {
        -webkit-appearance: none !important;
        background-position: center !important;
        background-size: /*[[button]]*/ !important;
        background-color: /*[[bg]]*/ !important;
    }
    ::-webkit-scrollbar-button:horizontal:increment {
        -webkit-appearance: none !important;
        background-position: center !important;
        background-size: /*[[button]]*/ !important;
        background-color: /*[[bg]]*/ !important;
    }
    ::-webkit-scrollbar-button:vertical:decrement:hover {
        -webkit-appearance: none !important;
        background-position: center !important;
        background-size: /*[[button]]*/ !important;
        background-color: /*[[bh]]*/ !important;
    }
    ::-webkit-scrollbar-button:vertical:increment:hover {
        -webkit-appearance: none !important;
        background-position: center !important;
        background-size: /*[[button]]*/ !important;
        background-color: /*[[bh]]*/ !important;
    }
    ::-webkit-scrollbar-button:horizontal:decrement:hover {
        -webkit-appearance: none !important;
        background-position: center !important;
        background-size: /*[[button]]*/ !important;
        background-color: /*[[bh]]*/ !important;
    }
    ::-webkit-scrollbar-button:horizontal:increment:hover {
        -webkit-appearance: none !important;
        background-position: center !important;
        background-size: /*[[button]]*/ !important;
        background-color: /*[[bh]]*/ !important;
    }
    ::-webkit-scrollbar-button:vertical:decrement:active {
        -webkit-appearance: none !important;
        background-position: center !important;
        background-size: /*[[button]]*/ !important;
        background-color: /*[[ba]]*/ !important;
    }
    ::-webkit-scrollbar-button:vertical:increment:active {
        -webkit-appearance: none !important;
        background-position: center !important;
        background-size: /*[[button]]*/ !important;
        background-color: /*[[ba]]*/ !important;
    }
    ::-webkit-scrollbar-button:horizontal:decrement:active {
        -webkit-appearance: none !important;
        background-position: center !important;
        background-size: /*[[button]]*/ !important;
        background-color: /*[[ba]]*/ !important;
    }
    ::-webkit-scrollbar-button:horizontal:increment:active {
        -webkit-appearance: none !important;
        background-position: center !important;
        background-size: /*[[button]]*/ !important;
        background-color: /*[[ba]]*/ !important;
    }
}
