css:
@-moz-document url-prefix("https://v1.mrpiracy"), url-prefix("https://mrpiracy"), url-prefix("https://www.mrpiracy") {
div#topbar, div#content, div#content div.content-box {
    background-color: #1b1b1b;
}
div#content {
    border-radius: 0px !important;
}
div#footer, .player-aux {
    background-color: #1b1b1b;
    color: #ffffff;
}
footer, nav, center {
    background:#1b1b1b;
}
.content-box h3 {
    color: #fff;
}
.server-choose-wrapper {
    background:#1b1b1b !important;
    color: #fff;
}
.box-header {
    background: #333333 !important;
    border: 1px solid #333333 !important;
    color: #fff !important;
}
div#content div#player-box div.player-aux div.movie-actions {
    border: 1px solid #333333;
    -moz-box-shadow: 0 1px 10px -1px #333333;
    -webkit-box-shadow: 0 1px 10px -1px #333333;
    box-shadow: 0 1px 10px -1px #333333;
    background: #333333;
}
.box-header li {
    background: #434343 !important;
    border: 1px solid #434343 !important;
    color: #fff !important;
}
.box-header li a {
    color: #fff !important;
    text-shadow: none !important;
    font-weight: 600;
}
.server-choose-wraper button {
    background: #757575 !important;
}
div#topBar div.topNav div.opts a {
    text-shadow: none !important;
}
.series-box-caption, .series-box-icon, .movies-box-caption, .movies-box-icon, .animes-box-caption, .animes-box-icon {
    color: #aba9a9 !important;
}

div.VPAID-container {
    display: none !important;
    opacity: 0 !important;
    visibility: hidden !important;
    pointer-events: none !important;
}
}
