css:
@-moz-document domain("4chan.org"), domain("4channel.org") {
@font-face {font-family: 'FontAwesome'; font-weight: normal; font-style: normal; src: url("https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.eot?v=4.7.0"); src: url("https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.eot?#iefix&v=4.7.0") format("embedded-opentype"), url("https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.woff2?v=4.7.0") format("woff2"), url("https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.woff?v=4.7.0") format("woff"), url("https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.ttf?v=4.7.0") format("truetype"), url("https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.svg?v=4.7.0#fontawesomeregular") format("svg");}
/*[[fontfamily]]*/

html, body {background: var(--background); color: var(--text);}
body {font-size: .92em; line-height: 1.43;}

:root {
/*[[link-color]]*/
/*[[quote-color]]*/
/*[[thm-color]]*/
/*[[thm-color-level]]*/
/*[[thm-scheme]]*/
}

::-webkit-scrollbar {background: var(--scroll-bg); height: 14px; width: 14px;}
::-webkit-scrollbar-track {background-clip: padding-box; border: solid transparent; border-width: 0;}
::-webkit-scrollbar-button {width: 0px; height: 0px;}
::-webkit-scrollbar-thumb {background-color: var(--scroll); background-clip: padding-box; border: 2px solid transparent; border-radius: 4px;}
::-webkit-scrollbar-thumb:hover {background-color: var(--scroll-hover);}
::-webkit-scrollbar-thumb:active {background-color: var(--scroll-active);}
::-webkit-scrollbar-corner {background: var(--scroll-bg);}

/*[[width-thread]]*/

*::selection {background: var(--link); color: var(--contrast-text);}
*::-moz-selection {background: var(--link); color: var(--contrast-text);}
*::-webkit-selection {background: var(--link); color: var(--contrast-text);}

a, a:visited {color: var(--link); transition: color .1s; text-decoration: none !important;}
a:hover {color: var(--link-hover) !important;}
a[savefrom_lm_is_link="1"] {background: none !important;}
a[savefrom_lm_is_link="1"]::before {content: '\f019'; font: 16px/1 FontAwesome;}

/* Main Page */
#logo-fp {font-size: 48px; line-height: 2; text-align: center;}
a[title="Home"]::before {color: var(--quote); content: "\f18c"; font: 56px/2 FontAwesome; margin-right: 8px;}
a[title="Home"]::after {color: var(--quote); content: "4chan"; font-weight: bold;}
a[href="//www.4channel.org/"]::after {content: "4channel";}
#logo-fp img {display: none;}

#announce {background: var(--post-bg); color: var(--text);}
.box-outer {border: none; border-radius: 3px; box-shadow: 0 0 0 1px var(--border); overflow: hidden;}
#announce .boxbar {background: var(--alt-post-bg); color: var(--text);}

.top-box {background: var(--post-bg);}
.boxbar {background: var(--alt-post-bg); color: var(--text);}

#filter-btn:hover, #opts-btn:hover {color: var(--link);}
#fp-menu {background: var(--post-bg); border-color: var(--border); border-radius: 3px; box-shadow: 0 1px 6px var(--shadow2);}
#fp-menu li:hover {background: var(--alt-post-bg);}
#fp-menu .fp-menu-sep {border-bottom-color: var(--border);}

h3[style="text-decoration: underline; display: inline;"] {color: var(--alt-text); display: inline-block !important; text-decoration: none !important; padding: 5px 0;}
.warning, font[color="red"] {color: var(--red);}
#boards a {color: var(--link);}
#boards a:hover {text-decoration: none;}

.c-thread img {border: none; border-radius: 3px;}

#ft li {background: var(--post-bg); border-color: var(--border) !important;}
#ft li.current, #ft li.fill {background: var(--post-bg); color: var(--border);}
#ft li a {color: var(--link);}

#backdrop {background-color: var(--shadow3);}
#disclaimer-dialog {background: var(--post-bg); border: none; border-radius: 3px; box-shadow: 0 0 0 1px var(--border), 0 6px 16px var(--shadow3); overflow: hidden;}
#disclaimer-dialog .hd {background: var(--alt-post-bg); color: var(--text);}
#disclaimer-dialog .container-close, .closebutton {background: none; color: var(--text); font-size: 0;}
#disclaimer-dialog .container-close:hover, .closebutton:hover {color: var(--link);}
#disclaimer-dialog .container-close::before, .closebutton::before {content: "\f00d"; font: 14px/1 FontAwesome;}

/* News */
.content {color: var(--text);}
.content h3 {background: var(--post-bg); border-color: var(--border); border-radius: 3px; color: var(--text);}
.center img {border-color: var(--border);}
img[style="display: block; margin: auto; border: 1px dashed;"] {border-color: var(--border) !important;}
em[style="color: grey;"], em[style="color: grey; text-align: center;"] {color: var(--alt-text) !important;}

hr {background: transparent; border-top-color: transparent; color: var(--border);}

.redb {color: var(--red);}

/* Blog */
#masthead {background: var(--background);}
#content article.post {background: var(--post-bg);}
#content article.post .post-meta {background: var(--post-bg); border-top-color: var(--border);}
#content article.post .post-meta a {color: var(--alt-text);}
h1, h2, h3, h4, h5, h6 {color: var(--text);}
.post-inner ol {color: var(--alt-text);}
.post-inner blockquote, .post-inner blockquote p {color: var(--alt-text);}
.post-inner blockquote {border-left-color: var(--border);}

#sidebar {background: var(--background); color: var(--alt-text);}
#sidebar h1, #sidebar h2, #sidebar h3, #sidebar h4, #sidebar h5, #sidebar h6 {color: var(--text);}
#sidebar .widget {border-bottom-color: var(--border);}

/* FAQ */
.boxcontent {color: var(--text); line-height: unset;}
.boxcontent a {color: var(--link);}

.left-box {background: var(--post-bg); color: var(--text);}
.left-box .boxbar {background: var(--alt-post-bg);}
.boxcontent>ul {color: var(--text);}

.right-box {background: var(--post-bg); color: var(--text);}
.right-box .boxbar {background: var(--alt-post-bg); color: var(--text);}
.right-box .boxbar a {color: var(--text) !important;}
dt {color: var(--alt-text);}
dt.first {color: var(--alt-text);}
.boxcontent dd {border-bottom-color: var(--border);}

/* Rules */
.boxcontent h3 a {color: var(--link);}

/* Pass */
hr[style="border: 0; color: #800; background: #800; height: 1px; margin-top: 1.0em; margin-bottom: 1.0em;"] {background: var(--border) !important; color: var(--border);}
.left-box hr {background: var(--border); color: var(--border);}

/* Press */
.bluehr {background: var(--border); color: var(--border);}
.left-box .boxbar a {color: var(--link);}

/* ===== Page ===== */

/* Board Bar */
div#boardNavDesktop {color: var(--alt-text-2);}
div#boardNavDesktop a {color: var(--alt-text);}
div#boardNavDesktop a:hover {color: var(--link) !important;}

#bannerCnt, .middlead, .adg-rects {display: none !important;}

/* Setting */
.UIPanel {background-color: var(--shadow3);}
.UIPanel > div {border-radius: 3px; scrollbar-color: var(--scroll) var(--post-bg); scrollbar-width: thin;}
.extPanel.reply {box-shadow: 0 0 0 1px var(--border), 0 6px 16px var(--shadow3);}

.yotsuba_new .panelHeader, .yotsuba_b_new .panelHeader {border-bottom-color: var(--border);}
.panelHeader .panelCtrl {transition: .1s;}
.panelHeader .panelCtrl:hover {color: var(--link);}
.panelHeader .panelCtrl::before {content: "\f00d"; font: 14px/1 FontAwesome; position: absolute; pointer-events: none; padding: 2px 0; width: 18px; text-align: center;}
.panelHeader .panelCtrl img {opacity: 0;}

.settings-cat-lbl {text-transform: uppercase; letter-spacing: .5px;}
.settings-cat-lbl img {display: none;}
.settings-cat-lbl img[src="//s.4cdn.org/image/buttons/futaba/post_expand_plus.png"] + .settings-expand.pointer::before, .settings-cat-lbl img[src="//s.4cdn.org/image/buttons/burichan/post_expand_plus.png"] + .settings-expand.pointer::before, .settings-cat-lbl img[src="//s.4cdn.org/image/buttons/futaba/post_expand_plus@2x.png"] + .settings-expand.pointer::before, .settings-cat-lbl img[src="//s.4cdn.org/image/buttons/burichan/post_expand_plus@2x.png"] + .settings-expand.pointer::before {content: "\f078"; font: 14px/1 FontAwesome; margin-right: 5px;}
.settings-expand.pointer::before {content: "\f077"; font: 14px/1 FontAwesome; margin-right: 5px;}
#settingsMenu .settings-cat {margin-left: 18px;}
.settings-tip {color: var(--alt-text-2);}
.settings-sub label:before {border-color: var(--alt-border);}

.extPanel input[type="text"], .extPanel textarea {border-color: var(--border);}

/* Create Post */
div.boardBanner {color: var(--text);}
div.boardBanner>div.boardTitle {font-family: inherit; letter-spacing: 0;}
div.boardBanner>div.boardSubtitle {font-size: small;}

div.closed {color: var(--red); padding-top: 50px; padding-bottom: 50px;}
#togglePostFormLink {color: transparent; font-size: 1.1em;}
#togglePostFormLink a, .open-qr-link {background: var(--link); border-radius: 3px; box-shadow: 0 2px 8px -4px var(--link); color: var(--alt-btn-text) !important; font-weight: 400; padding: 7px 14px; transition: .1s;}
#togglePostFormLink a:hover, .open-qr-link:hover {background: var(--link-hover); box-shadow: 0 2px 8px -4px var(--link-hover); color: var(--post-bg) !important;}

#postForm {border-spacing: 2px;}
table.postForm>tbody>tr>td:first-child {background-color: unset; border: none; color: var(--alt-text);}

input[type=text], input[type=password], table.postForm>tbody textarea, #recaptcha_response_field {background: var(--alt-btn-bg); border: 1px solid var(--border); border-radius: 3px; color: var(--text); font-family: inherit; font-size: unset; padding: 4px 8px; transition: border .1s, background .1s;}
input[type=text]:focus, input[type=password]:focus, input:not([type]):focus, textarea:focus {background: var(--alt-btn-bg-hover) !important; border-color: var(--alt-border) !important;}
input[type="button"], input[type="submit"], button {background: var(--btn-bg); border: 1px solid var(--border); border-radius: 3px; color: var(--text); cursor: pointer; font-family: inherit; padding: 4px 8px; transition: .1s; outline: none;}
input[type="button"]:hover, input[type="button"]:focus, input[type="submit"]:hover, input[type="submit"]:focus, button:hover, button:focus {background: var(--btn-bg-hover); border-color: var(--border) !important;}
select {-webkit-appearance: none; -moz-appearance: none; appearance: none; background: var(--alt-btn-bg); background-image: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" width="18" height="44"><defs><g><path id="select" d="M10.3 0L5.5 4.7.7 0 0 .7 5.5 6 11 .7z"/></g></defs><use href="%23select" x="0" fill="%23000"/><use href="%23select" x="0" y="22" fill="%23fff"/></svg>'); background-position: var(--select); background-repeat: no-repeat; border: 1px solid var(--border); border-radius: 3px; color: var(--text); font-family: inherit; padding: 4px 22px 4px 8px; outline: none;}
input:disabled {color: var(--alt-text-2);}

/*[[hide-news]]*/

/* Oekaki */
#tegaki {background-color: var(--border); color: var(--text); font-family: inherit;}
#tegaki>div {background-color: var(--post-bg);}
#tegaki-menu-bar {border-right-color: var(--border);}
.tegaki-toolmode-grp {border-left-color: var(--border);}

.tegaki-mb-btn {cursor: pointer;}
.tegaki-mb-btn:hover:not(.tegaki-disabled), .tegaki-ui-btn:hover:not(.tegaki-disabled) {background-color: var(--btn-bg-hover);}

.tegaki-sw-btn {border: 1px solid var(--border); border-radius: 3px; box-shadow: none; cursor: pointer; padding: 3px 6px;}
.tegaki-sw-btn:hover:not(.tegaki-sw-btn-a) {background-color: var(--btn-bg-hover);}
.tegaki-sw-btn-a {background-color: var(--alt-post-bg);}

.tegaki-tool-btn {cursor: pointer;}
.tegaki-tool-btn:hover {background-color: var(--btn-bg-hover);}
.tegaki-tool-active {color: var(--link);}

.tegaki-ctrlgrp-title {background-color: var(--alt-post-bg);}
.tegaki-ui-btn {cursor: pointer;}
.tegaki-disabled {cursor: default;}

#tegaki-layers-grid {background-color: var(--post-bg); border-color: var(--border);}
.tegaki-layers-cell-s, .tegaki-layers-cell-a {background-color: var(--alt-post-bg); box-shadow: 0 1px 0 0 var(--border);}
.tegaki-ui-cb {border-color: var(--text);}
.tegaki-ui-cb-w:hover .tegaki-ui-cb::after, .tegaki-ui-cb:hover::after {background-color: var(--alt-text-2);}
.tegaki-ui-cb-a::after {background-color: var(--text);}
.tegaki-ui-cb-w:hover .tegaki-ui-cb-a::after, .tegaki-ui-cb-a:hover::after {background-color: var(--text);}
.tegaki-layers-cell {box-shadow: 0 1px 0 0 var(--border);}

#tegaki-size-lbl, #tegaki-alpha-lbl {background-color: var(--alt-btn-bg); border: 1px solid var(--border); border-radius: 3px; color: var(--text); font-family: inherit; outline: none; transition: background .1s;}
.tegaki-stealth-input:hover:not(.tegaki-disabled) {background-color: var(--alt-btn-bg-hover);}

/* Nav Links */
#search-box {padding: 4px 8px;}

#ctrl-top, .navLinks {color: transparent;}
.navLinks a, #ctrl-top a {color: var(--nav-link);}
.navLinks a:hover, #ctrl-top a:hover {color: var(--nav-hover);}
.navLinks label, .navLinks span, .thread-stats {color: var(--text);}

a[href="#top"]::before, #totop span::before {background-color: transparent; content: ""; display: block; height: 50%; width: 35px; position: fixed; top: 0; left: 0; text-align: center;}
a[href="#top"]:hover::before, #totop span:hover::before {background: linear-gradient(to top, transparent, var(--post-bg));}
a[href="#top"]::after, #totop span::after {color: var(--alt-text-2) !important; content: "\f106"; display: block; font: bold 26px/100px FontAwesome; height: 100px; width: 35px; position: fixed; top: 0; left: 0; text-align: center;}
a[href="#bottom"]::before, #tobottom span::before {background-color: transparent; color: var(--nav-link) !important; content: ""; display: block; height: 50%; width: 35px; position: fixed; bottom: 0; left: 0; text-align: center;}
a[href="#bottom"]:hover::before, #tobottom span:hover::before {background: linear-gradient(to top, var(--post-bg), transparent);}
a[href="#bottom"]::after, #tobottom span::after {color: var(--alt-text-2) !important; content: "\f107"; display: block; font: bold 26px/100px FontAwesome; height: 100px; width: 35px; position: fixed; bottom: 0; left: 0; text-align: center;}
a[href="#top"] + div + a::before, a[href="#top"] + div + a::after {display: none;}
a[data-cmd="update"]::before {content: ""; height: 80px; width: 35px; position: fixed; margin-top: -40px; top: 50%; left: 0; z-index: 210;}
a[data-cmd="update"]:hover::before {background: linear-gradient(0deg, var(--background) 0%, var(--post-bg) 30%, var(--post-bg) 70%, var(--background) 100%);;}
a[data-cmd="update"]::after {box-sizing: border-box; content: "\f021"; font: 19px/1 FontAwesome; color: var(--alt-text-2) !important; padding: 0 2px; position: fixed; margin-top: -10px; top: 50%; left: 7px; width: 20px; z-index: 211; transition: .3s;}
a[data-cmd="update"]:hover::after {color: var(--alt-text) !important; transform: rotate(360deg);}

input[type="checkbox"] {cursor: pointer; height: 15px; width: 15px; -webkit-appearance: none; -moz-appearance: none; appearance: none; vertical-align: middle; outline: none; margin-top: -1px;}
input[type="checkbox"]::before {background: var(--alt-btn-bg); border: 1px solid var(--alt-border); border-radius: 3px; box-sizing: border-box; content: ""; display: block; height: 15px; width: 15px; transition: .1s;}
input[type="checkbox"]:focus::before {background: var(--btn-bg-hover);}
input[type="checkbox"]::after {color: transparent; content: "\f00c"; display: block; font: 12px/15px FontAwesome; height: 15px; width: 15px; position: relative; bottom: 15px; text-align: center; transition: .1s;}
input[type="checkbox"]:checked::before {background: var(--highlight-bg); border-color: var(--alt-border);}
input[type="checkbox"]:checked::after {color: var(--link);}

/* Posts */
.party-hat {display: none;}

.preview {background: var(--post-bg); border: none !important; box-shadow: 0 0 0 1px var(--border), 0 1px 6px var(--shadow2) !important;}
#quote-preview {padding: 4px 8px;}
.yotsuba_new #quote-preview.highlight, .yotsuba_b_new #quote-preview.highlight {background: var(--highlight-bg); border: none !important; box-shadow: 0 0 0 1px var(--highlight), 0 1px 6px 0 var(--highlight-shadow) !important;}

hr + .thread {border-top: 1px solid var(--border); margin-top: 25px;padding-top: 25px;}
div.thread {counter-reset: postcount;}
div.op {background: var(--post-bg); border: none; border-radius: 3px; display: inline-block;}
.omittedposts, .abbr {color: var(--alt-text);}
span.summary {color: var(--alt-text); display: block; margin: -4px 8px 4px;}
div.post {border-radius: 3px; box-shadow: 0 0 0 1px var(--border), 0 1px 3px 0 var(--shadow); margin: 8px 0; padding: 4px 8px; transition: background .1s, border .1s, box-shadow .1s; overflow: unset; position: relative;}
div.sideArrows {display: none;}
div.reply {background-color: var(--post-bg); border: none;}
.post.reply {margin: 10px;}
.reply:target, .reply.highlight {background: var(--highlight-bg) !important; border: none !important; box-shadow: 0 0 0 1px var(--highlight), 0 1px 6px 0 var(--highlight-shadow); padding: 4px 8px;}

/*[[report-checkbox]]*/
div.post div.postInfo input[type="checkbox"] {margin: 0 3px 0 0;}
div.post div.postInfo {color: var(--alt-text-2); font-size: .92em;}
div.post div.postInfo span.subject {color: var(--title);}
.nameBlock.capcodeMod span.name, span.capcodeMod a span.name, span.capcodeMod span.postertrip, span.capcodeMod strong.capcode {color: var(--postertrip) !important;}
div.post div.postInfo span.nameBlock span.postertrip {color: var(--quote) !important;}
div.post div.postInfo span.nameBlock span.name {color: var(--alt-text-2); font-weight: 400;}

#tooltip {background-color: var(--alt-text); color: var(--alt-btn-text); border-radius: 3px;}
.tip-top-left:before, .tip-top-right:before, .tip-top:before {border-top-color: var(--alt-text);}

span.postNum::before {counter-increment: postcount; content: "#" counter(postcount); margin-left: .5em; margin-right: 1em;}
div.post div.postInfo span.postNum a {color: var(--alt-text);}
div.post div.postInfo span.postNum a:hover, .posteruid .hand:hover {color: var(--link) !important;}
a.replylink:not(:hover), div#absbot a:not(:hover) {color: var(--link) !important;}
.yotsuba_new .postMenuBtn, .futaba_new .postMenuBtn, .postMenuBtn {color: var(--alt-text-2);}
.yotsuba_new .postMenuBtn:hover, .futaba_new .postMenuBtn:hover, .postMenuBtn:hover {color: var(--alt-text) !important;}
div.backlink {font-style: italic;}
.burichan_new .backlink a, .yotsuba_b_new .backlink a, .backlink a {color: var(--alt-link) !important;}
.burichan_new .backlink a:hover, .yotsuba_b_new .backlink a:hover, .backlink a:hover {color: var(--alt-hover) !important;}

.dd-menu {font-size: 13px;}
.dd-menu ul {background-color: var(--post-bg); border-color: var(--border); border-right-width: 1px; border-radius: 3px; box-shadow: 0 1px 6px var(--shadow2); padding: 4px 0;}
.dd-menu li {padding: 4px 10px; border-bottom: none;}
.dd-menu li:hover {background-color: var(--alt-post-bg);}
.dd-menu li a {color: var(--text) !important;}

#yt-preview {border-radius: 3px; padding: 0; box-shadow: 0 1px 6px 0 var(--shadow3); overflow: hidden;}

/*[[image-filter]]*/
div.post div.file .fileThumb img {border-radius: 3px;}
.fileText {color: var(--alt-text); font-size: .8em;}
.fileText a {font-size: 1.2em;}

div.post blockquote.postMessage {margin: 8px 20px;}
.quoteLink, .quotelink {color: var(--link) !important;}
.deadlink {color: var(--alt-link) !important;}
blockquote>span.quote {color: var(--quote);}
s, s a:not(:hover) {background: var(--spoiler) !important; color: var(--spoiler) !important; transition: .1s;}
s:hover, s:focus, s:hover a {color: var(--text) !important;}

/* Quick Reply */
#quickReply {border-radius: 3px; box-shadow: 0 0 0 1px var(--border), 0 6px 16px var(--shadow3); padding: 4px;}
#qrepHeader, #qrHeader {background: none; border: none; color: var(--text); padding: 2px 0;}
#qrHeader {position: relative;}
#qrHeader::after {content: "\f00d"; font: 14px/1 FontAwesome; pointer-events: none; position: absolute; top: 3px; right: 4px;}
#qrClose {opacity: 0;}

#quickReply input[type="text"], #quickReply textarea, #quickReply #recaptcha_response_field {border-color: var(--border); font-family: inherit; padding: 4px 8px; margin: 0 0 2px 0;}
textarea {background-color: var(--alt-btn-bg); border-color: var(--border); border-radius: 3px; color: var(--text); transition: border .1s, background .1s; outline: none;}

.yotsuba_new #qrFile {color: var(--text);}

/* Footer */
div.pagelist {background: var(--post-bg); border: 1px solid var(--border); border-radius: 3px; color: var(--alt-text-2);}
div.pagelist div.cataloglink {border-left-color: var(--border);}

div#boardNavDesktopFoot {color: var(--alt-text-2);}
div#boardNavDesktopFoot a {color: var(--alt-text);}
div#boardNavDesktopFoot a:hover {color: var(--link) !important;}
div#absbot {color: var(--alt-text);}

/* Catalog */
.btn-wrap:before, .btn-wrap:after {color: transparent;}

.button {color: var(--link);}
.button:hover {color: var(--link-hover);}

.error {color: var(--red);}

.extended-small .thread {background: var(--post-bg); border-radius: 3px; margin: 5px; height: 320px; box-shadow: 0 0 0 1px var(--border), 0 1px 3px 0 var(--shadow);}
.extended-large .thread {background: var(--post-bg); border-radius: 3px; margin: 5px; height: 410px; box-shadow: 0 0 0 1px var(--border), 0 1px 3px 0 var(--shadow);}
.thumb {border-radius: 3px; box-shadow: none;}

#post-preview {background-color: var(--post-bg); box-shadow: 0 0 0 1px var(--border), 0 1px 6px 0 var(--shadow2); color: var(--text); font-family: inherit;}
.post-subject {color: var(--sage);}
.post-author {color: var(--quote);}
.post-page {color: var(--alt-text);}
.post-last {color: var(--alt-text-2);}

.teaser {font-size: 14px;}

/* Archive */
table {border-spacing: 2px;}
#arc-list {background: var(--post-bg); border-radius: 3px; box-shadow: 0 0 0 1px var(--border), 0 1px 3px 0 var(--shadow); overflow: hidden;}
