#de-panel-info-icount::before, #de-panel-info-acount:not(:empty)::before {color: var(--alt-text-2); content: "•"; padding: 0 2px;}

/* Windows */
#de-win-cfg, #de-win-fav, #de-win-hid, #de-win-vid {background: var(--post-bg); border-radius: 3px; box-shadow: 0 0 0 1px var(--border), 0 6px 16px var(--shadow3); z-index: 981 !important;}
#de-win-cfg {width: 460px;}
#de-win-cfg.de-win-fixed {box-shadow: 0 0 0 1px var(--border), 0 1px 6px var(--shadow2);}

.de-win-head {background: none; border-radius: 0; color: var(--text); font-family: inherit; font-size: 15px; padding: 6px;}
.de-win-buttons > svg {transition: .1s; border-radius: 3px;}
.de-win-buttons > svg:hover {background-color: var(--alt-post-bg); box-shadow: 0 0 0 1px var(--alt-post-bg);}

#de-win-cfg > .de-win-body {box-shadow: none;}

#de-cfg-bar {background-color: var(--alt-post-bg); border-radius: 3px; padding: 3px; margin: 0 3px;}
.de-cfg-tab {background: var(--alt-post-bg) !important; border: none !important; border-radius: 3px !important; color: var(--alt-text); cursor: pointer; font-family: inherit; letter-spacing: .5px; text-transform: uppercase;}
.de-cfg-tab:hover {background: none !important; color: var(--text);}
.de-cfg-tab[selected], .de-cfg-tab[selected]:hover {background: var(--alt-text) !important; color: var(--alt-btn-text);}

.de-cfg-body {font-family: inherit !important; font-size: 14px !important;}
.de-cfg-body, #de-cfg-buttons {border: none;}

#de-spell-editor {border: 1px solid var(--border); border-radius: 3px; padding: 0; margin: 2px 0;}
#de-spell-rowmeter {background-color: var(--table-tr); color: var(--text); font-family: Consolas, monospace;}
#de-spell-txt {font-family: Consolas, monospace;}

.de-cfg-inptxt, .de-cfg-label, .de-cfg-select {font-family: inherit !important; font-size: 14px !important;}
.de-cfg-chkbox {margin: 3px 1px !important; vertical-align: middle !important;}
