.de-cfg-select {padding: 3px 22px 3px 6px !important;}
.de-cfg-button {font-family: inherit !important; font-size: 14px !important; padding: 3px 6px !important; height: unset;}

#de-cfg-button-debug {font-size: 14px; padding: 3px 6px; font-family: inherit;}
#de-info-log {border-left-color: var(--border);}
.de-info-row {padding: 2px 0;}

.de-button {font-family: inherit; font-size: 14px; padding: 3px 6px !important; height: auto;}

/* Bookmarks */
#de-win-fav > .de-win-body, #de-win-hid > .de-win-body, #de-win-vid > .de-win-body {border: none;}
#de-win-fav .de-win-body {background-color: var(--theme_default_postbg) !important;}

.de-fold-block {border: none;}
.de-fav-header {background-color: var(--table-tr); border-radius: 3px; font-size: 14px; padding: 3px 0; margin-bottom: 3px;}
.de-fav-del-btn > svg {height: 15px; width: 15px; vertical-align: -3px;}
.de-fav-entries {border: none; border-left: 2px solid var(--border); margin-left: 6px;}
.de-entry {box-shadow: none !important; font-size: 14px; margin: 3px 0 !important;}

.de-fav-del-btn > svg {color: var(--alt-text);}
.de-fav-del-btn > svg:hover {color: var(--text);}
.de-fav-del-btn[de-checked] > svg {color: var(--red);}

.de-fav-inf {font-family: inherit;}
.de-fav-inf-old {color: var(--alt-text);}
.de-fav-inf-new {color: var(--link);}
.de-fav-inf-you {background-color: var(--link); color: var(--alt-text); opacity: 1;}

#de-fav-del-confirm {padding-top: 6px;}

/* Page */
.de-fullimg-center {background-color: var(--alt-post-bg); border: none !important; box-shadow: 0 6px 16px var(--shadow4);}
.de-fullimg-center video {outline: none;}
.de-img-btn {border-radius: 3px 0 0 3px; color: var(--alt-text);}
.de-img-btn > svg {height: 20px; width: 20px; margin: 8px;}
