#de-img-btn-auto {margin-top: 62px;}
.de-img-btn-auto-on {color: var(--link);}

.de-fullimg-info {background-color: var(--post-bg); border-radius: 10px; box-shadow: 0 0 0 1px var(--border), 0 1px 6px var(--shadow2); padding: 1px 8px;}
.de-fullimg-info > .de-btn-src {color: var(--alt-text-2);}
.de-fullimg-link {color: var(--alt-text) !important; font-family: inherit; font-size: 13px;}
.de-fullimg-link:hover {background: none; color: var(--text) !important;}

/* Create Post */
.de-parea > div:first-child {color: transparent;}
.de-parea-btn-thr, .de-parea-btn-close, .de-parea-btn-reply {background-color: var(--link); border-radius: 3px; box-shadow: 0 2px 8px -4px var(--link); color: var(--alt-btn-text) !important; display: inline-block; font-size: 1.1em; padding: 5px 14px; transition: .1s;}
.de-parea-btn-thr:hover, .de-parea-btn-close:hover, .de-parea-btn-reply:hover {background-color: var(--link-hover); box-shadow: 0 2px 8px -4px var(--link-hover); color: var(--alt-btn-text) !important;}

.de-textarea {padding: 4px 8px !important; transition: background .1s, border .1s !important;}

.de-markup-back {fill: var(--btn-bg); stroke: var(--border); stroke-width: 1;}
#de-txt-panel {fill: var(--text);}
#de-txt-panel div {transition: .1s;}
#de-txt-panel div:hover  {fill: var(--link);}

.de-file {background: none; border-color: var(--border); border-radius: 3px; border-width: 2px; overflow: hidden;}
.de-file > .de-file-utils {background: var(--post-bg);}
.de-file-off > .de-file-img > div::after {font-family: inherit;}

/* Quick Reply */
#de-win-reply.de-win-inpost {box-shadow: 0 0 0 1px var(--border), 0 1px 3px var(--shadow); position: unset;}
#de-win-reply.de-win-inpost > .de-win-body {padding: 4px;}
#de-win-reply.de-win {border-radius: 3px; box-shadow: 0 0 0 1px var(--border), 0 6px 16px var(--shadow3); z-index: 980 !important;}
#de-win-reply.de-win > .de-win-body {border: none; padding: 4px;}

/* Thread */
.de-oppost.de-mypost {margin-left: unset !important;}
.de-oppost.de-mypost::before {content: ""; display: block; position: absolute; top: 2px; bottom: 2px; left: 2px; border-left: 2px solid var(--highlight) !important;}

.de-mypost {box-shadow: 0 0 0 1px var(--border), 0 1px 3px var(--shadow) !important; margin-left: 22px !important;}
.de-mypost::before {content: ""; display: block; position: absolute; top: 0; bottom: 0; left: -10px; border-left: 2px solid var(--highlight) !important;}
.de-mypost-reply {border-left-color: transparent !important; margin-left: 22px !important;}
.de-mypost-reply::before {display: block; border-left: 2px dashed var(--highlight) !important; left: -10px;}

.de-new-post {box-shadow: 0 0 0 1px var(--border), 0 1px 3px var(--shadow) !important; margin-top: 18px !important;}
.de-new-post::after {border-top: 2px solid var(--link); clear: both; content: ''; display: block; position: absolute; top: -10px; left: 0; width: 100%;}
.de-selected {background: var(--highlight-bg); box-shadow: 0 0 0 1px var(--highlight), 0 1px 6px var(--highlight-shadow) !important;}
.de-input-error {background: var(--post-bg); box-shadow: 0 0 0 1px var(--red), 0 2px 8px -4px var(--red) !important;}

.de-pview {border-color: transparent !important; box-shadow: 0 0 0 1px var(--border), 0 1px 6px var(--shadow2) !important; padding: 4px 8px; position: absolute !important;}
.de-pview.de-mypost::before, .de-pview.de-mypost-reply::before {top: 2px; bottom: 2px; left: 1px;}

.de-runned span.postNum::before {display: none;}

.de-btn-expthr, .de-btn-fav, .de-btn-fav-sel, .de-btn-hide, .de-btn-hide-user, .de-btn-reply, .de-btn-sage, .de-btn-src, .de-btn-stick, .de-btn-stick-on, .de-btn-unhide, .de-btn-unhide-user, .de-win-btn-clear, .de-win-btn-close, .de-win-btn-toggle {height: 18px; width: 18px; margin: 0 2px -4px 0 !important;}
.de-btn-expthr, .de-btn-fav, .de-btn-fav-sel, .de-btn-hide, .de-btn-hide-user, .de-btn-reply, .de-btn-src, .de-btn-stick, .de-btn-stick-on, .de-btn-unhide, .de-btn-unhide-user {fill: transparent;}
.de-btn-expthr, .de-btn-fav, .de-btn-hide, .de-btn-reply, .de-btn-sage, .de-btn-src, .de-btn-stick, .de-btn-unhide {color: var(--alt-text-2);}
.de-btn-hide-user {color: var(--text);}
.de-btn-unhide-user {color: var(--text);}
.de-btn-stick-on {color: var(--text);}

.de-btn-fav-sel {color: var(--link);}
.de-btn-sage {fill: transparent; color: var(--sage);}

.de-post-counter {color: var(--alt-text-2); font-family: inherit; font-size: 14px; font-weight: normal; vertical-align: 0;}
.de-post-counter-you {color: var(--link); font-family: inherit; font-size: 13px; float: left; margin: 2px 4px 2px 0;}
.de-post-note:not(:empty) {font-family: inherit;}

.de-menu {border: none !important; box-shadow: 0 0 0 1px var(--border), 0 1px 6px var(--shadow2) !important; padding: 4px 0 !important;}
.de-menu-item {padding: 4px 10px; font-family: inherit;}
.de-menu-item:hover {background-color: var(--alt-post-bg); color: var(--text) !important;}
.de-menu div[style="display: inline-block; border-right: 1px solid grey;"] {border-right-color: var(--border) !important;}

.de-video-thumb {border-radius: 3px;}
a.de-video-player::after {transition: .1s;}
.de-video-resizer::after {color: var(--text);}

.de-link-parent {outline: none !important; border-bottom: 1px dashed var(--link);}

.de-refmap {font-size: 80%; margin: 10px 0 0 18px;}
.de-refmap::before {display: none;}
.de-refcomma {color: transparent;}
.de-refmap a {color: var(--alt-link);}
.de-refmap a:hover {color: var(--alt-hover);}

.de-thr-updater {color: transparent;}
.de-thr-updater span {color: var(--theme_default_text);}

/* Pop-Up */
#de-wrapper-popup {border-radius: 3px;  font-family: inherit; overflow: visible !important; margin: 5px;}
.de-popup {border: none !important; box-shadow: 0 0 0 1px var(--border), 0 6px 16px var(--shadow3); margin: 0 !important;}
.de-popup-btn {color: var(--text);}
.de-editor {font-family: 'Consolas', monospace;}

div[style="font: bold 12px arial;"] {font-family: inherit !important;}

