css:
@-moz-document regexp(".*?blog\\.csdn\\.net.*?") {
/*
* 清理 CSDN 博客
*/
.hide-article-box, .recommend-box,.meau-list,#adContent, #csdn-toolbar, aside, #reportContent, .recommend-right,.pulllog-box,div[class="opt-box text-center"],a[data-type="report"],a[data-type="readreply"],.comment-like,a[data-type="vip"],a[data-type="app"],a[data-type="cs"],.login-mark,.login-box,.hljs-button,.option-box[data-type="regard"],.template-box,.reward-user-box,.blog-column-pay,#blogColumnPayAdvert,#blog_detail_zk_collection,.more-toolbox,.comment-edit-box,.right-message,.operating,.comment-box .comment-list-container .comment-list-box ul.comment-list li.comment-line-box .comment-tag,.new-opt-box,#csdn-userTooltip,.identity-icon,.expert,.bottom-pub-footer
  {
    display: none!important;
  }

  .container#mainBox main
  {
    margin: 0 auto!important;
    width: 100%!important;
  }

  #article_content
  {
    height: auto!important;
  }

  .comment-list-box
  {
    max-height: none!important;
  }

  .replay-box
  {
    display: list-item!important;
  }

  .article-bar-bottom
  {
    border-bottom: 0px!important;
  }
  .comment-box {
    padding-bottom: 0px;
  }
}
