css:
@-moz-document domain(lydsy.com)
{
  
body{
    margin-left: 15%;
    margin-right: 15%;
    background-image: url('/*[[bgp]]*/');
    background-attachment: fixed;
    background-position: center top;
    background-repeat: no-repeat;
    background-size: cover;
    line-height:2;
}
div.content{
  opacity: 0.8;
  background-color:#e4f0f8;
  border-radius: 10px;

  padding-top:10px;
  padding-bottom: 10px;
}
img{
  transition: all 0.2s;
  opacity: 1;
}
img:hover{
  transform: scale(1.1,1.1);
}
span.sampledata{
  background-color: #ffffff00;
  line-height:0.8;
}

h2{
  color:/*[[titlecol]]*/;
  font-size: 25;
  margin-top: 30px;
  margin-bottom: 5px;
}
span.green{
  color:/*[[titlecol]]*/;
}
span.red{
  color:/*[[titlecol]]*/;
}
a{
    font-size: 18;
}
a:link{
    color: /*[[linkcol]]*/;
}
a:visited{
    color: /*[[linkcol]]*/;
}
a{
  transition: color 0.3s;
}
a:hover{
  color: /*[[selcol]]*/;
}
th{
  opacity: 1;
}
tr.evenrow{
  background-color: /*[[evencol]]*/df;
}
tr.oddrow{
  background-color: /*[[oddcol]]*/df;
}
textarea{
  background-color: #ffffffaa;
  width: 70%;
}
div.notice{
  opacity: /*[[opa]]*/;
}
input{
  opacity: /*[[opa]]*/;
}
select{
  opacity: /*[[opa]]*/;
}
center{
  color:/*[[titlecol]]*/;
}
  
}
