        font-size: 1.2em !important;
    }

    /* 一覧 */
    div.newsFeed_item_text,
    div.newsFeed_item_title
    {
        /*font-size: 100% !important;*/
/*        font-size: 1.8rem !important; */
        font-size: 1.2em !important;
    }



}
