css:
@-moz-document domain("facebook.com")
{
body, #srp, #tbo, #vasq, #rcnt
  {
    background-image: url(http://www.hinhviet.com/images/2016/08/28/1.jpg);
    background-attachment: fixed;
    background-size: 100% 100%;
  }

  div.ab_tnav_wrp, div.rc, .hdtb_msb, #top_nav, #hdtb
  {
    background-color: rgba(255,255,255,0.7) !important;
    border-radius: 2%;
  }

  #appbar, #hdtbSum, #hdtb
  {
    background-color: rgba(255,255,255,0.7) !important;
  }

  #topbar, #extbar, .ab_tnav_wrp, ._ucd:hover, #hdtbSum
  {
    background-color: rgba(0,0,0,0) !important;
  }

  .sbibod, .sfsbc, .gstl_0, .rg_fbl, .rg_fbl:hover, .str.hmov
  {
    border: solid rgba(0,0,0,0) 1px !important;
    background-color: rgba(0,0,0,0) !important;
    box-shadow: none !important;
  }

  ._ucd
  {
    color: black;
    background-color: rgba(255,255,255,0.1) !important;
    border: none !important;
  }

  ._ucd:hover
  {
    background-color: rgba(255,255,255,0.3) !important;
  }
}
