css:
@-moz-document domain("erai-raws.info") {
/* this part changes the color of the text */
.entry-title a:link, .entry-title a:visited {
	color: #FFF !important;
	text-decoration: none;
}
/* this part changes the table size */
.col-lg-9 {
	-ms-flex: 0 0 75%;
	flex: 0 0 75%;
	max-width: 100%;
}

/* this crops the image */
img {
	height: 250px;
	margin: -70px 0 -15px 0;
}


/* this part removes adds and messages */
#main > div:nth-child(1) {display:none !important;}
.col-xs-8 {display:none !important;}
.col-xs-4 {display:none !important;}
}
