@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
domain("hattrick.ws"),
domain("hattrick.interia.pl"),
domain("hattrick.uol.com.br"),
domain("hattrick.org") {


/* 
 ********
 * HTML *
 ********
 */
 a {
    color:/*[[menu]]*/!important;
}
a:hover {
    color:/*[[headers]]*/!important;
    text-decoration:none!important;
}
a.shy {
    color:/*[[headers]]*/!important;
    font-style: italic;
}
a.bookmarkedUserWrote {
    color:#F06C00!important;
}
span.date {
    color:#F06C00!important;
    font-weight:bold!important;
}
.hattrick, .hattrickNoSupporter, #page {
    width:987px;
}
body {
    background: /*[[wall]]*/;
}
img[src*="/micro_star.gif"] {
    padding: 15px 0 0 16px!important;
    width: 0 !important;
    height: 0 !important;
    background-image: url(http://nrgjack.altervista.org/_altervista_ht/style/stelle/b_star.png) !important;
}
img[src*="/page/master_page.png"] {
    visibility:hidden !important;
}
#top {
    display:none!important;
}
#page {
    border: 1px solid /*[[borders]]*/!important;
    margin:2px auto!important;
    -moz-border-radius : 9px !important;
    -webkit-border-radius : 9px !important;
}
#header, #content, #bottom, #page {
    background: /*[[background]]*/ none no-repeat scroll left top !important;
}
input[id*="Stats"][type="text"] {
    width:95px;
}
/* 
 **********
 * HEADER *
 **********
 */
 #teamLinks, #online, #time {
}
#teamLinks a, #online, #time, #ticker a, #footer a {
    color:#ccc!important;
}
#teamLinks a {
    text-decoration:none!important;
    font-weight:bold!important;
    font-size:larger!important;
}
#teamLinks a:hover, #ticker a:hover {
    color:#fff!important;
}
#logo, #stageLogo {
    background: /*[[logo]]*/
    left:0px!important;
}
#stageLogo {
    height:25px!important;
    padding-bottom:20px!important;
    margin-top:auto!important;
}
#ticker div {
    padding-left: 18px!important;
    background-image: url(http://nrgjack.altervista.org/_altervista_ht/style/stelle/y_star.png) !important;
}
#ticker {
    left : 265px!important;
}
div.scContainer a:hover {
    opacity:0.6!important;
}
.subMenuBox h3, .boxBody h2, h3.feed {
    color:/*[[background]]*/!important;
    background: #ccc!important;
    border: double /*[[headers]]*/ !important;
    text-align:center!important;
    -moz-border-radius : 9px !important;
    -webkit-border-radius : 9px !important;
    padding:3px!important;
	opacity:/*[[opacity]]*/!important; 
}
/* 
 ********
 * MENU *
 ********
 */
 #menu {
    background: /*[[menu]]*/ none no-repeat scroll left top !important;
    -moz-border-radius: 9px;
    -webkit-border-radius: 9px;
    border: 1px solid /*[[borders]]*/;
   
    /*padding-left:3px!important;*/

	opacity:/*[[opacity]]*/!important; 
}
#menu a {
    font-size:larger!important;
    color: #ccc !important;
}
#menu a[href*="/Forum/Overview.aspx"] {
    color:#FF9900!important;
}
#menu a:hover {
    background: black !important;
    color: #fff !important;
}
#menu .menu-submenu a {
    line-height: 20px;
}

#menu .menu-submenu {
    border-right: 1px solid /*[[borders]]*/ ;    
    border-bottom: 1px solid /*[[borders]]*/ ;
    border-left: 1px solid /*[[borders]]*/ ;

}

/* 
 ***********
 * BOX TOP *
 ***********
 */
.sidebarBox .boxHead, .subMenuBox .boxHead, .main .boxHead 
{
        border: 0px !important;
}

.sidebarBox .boxHead h2, .subMenuBox .boxHead h2, .main .boxHead h2, .sidebarBox .boxHead .boxLeft h2, .subMenuBox .boxHead .boxLeft h2, .main .boxHead .boxLeft h2 {
    background: /*[[menu]]*/ none no-repeat scroll left top !important;
    border: 1px solid /*[[borders]]*/!important;
    color:#ccc!important;

	opacity:/*[[opacity]]*/!important; 
}
#header, .sidebarBox .boxHead h2, .subMenuBox .boxHead h2, .main .boxHead h2, .sidebarBox .boxHead .boxLeft h2, .subMenuBox .boxHead .boxLeft h2, .main .boxHead .boxLeft h2 {
    -moz-border-radius-topleft : 9px !important;
    -webkit-border-top-left-radius : 9px !important;
    -moz-border-radius-topright : 9px !important;
    -webkit-border-top-right-radius : 9px !important;
    margin-left:0px !important;
}
.sidebarBox .boxHead, .subMenuBox .boxHead, .main .boxHead, .sidebarBox .boxHead .boxLeft, .subMenuBox .boxHead .boxLeft, .main .boxHead .boxLeft {
    background: transparent none no-repeat scroll left top !important;
}
/* 
 **************
 * BOX CENTER *
 **************
 */
 .main .boxHead h2 span, .main .boxHead h2 a {
    color:#ccc!important;
}
.main .boxHead h2 a:hover {
    color:#fff!important;
}
.sidebarBox .boxBody, .subMenuBox .boxBody, .main .boxBody {
    background:#fff none repeat scroll 0 0!important;
    border-left:1px solid /*[[borders]]*/!important;
    border-right:1px solid /*[[borders]]*/!important;
}
/* 
 **************
 * BOX BOTTOM *
 **************
 */
 .sidebarBox .boxFooter, .subMenuBox .boxFooter, .main .boxFooter {
    background: #fff none no-repeat scroll left top !important;
    border-top: none !important;
    border-left: 1px solid /*[[borders]]*/!important;
    border-right: 1px solid /*[[borders]]*/!important;
    border-bottom: 1px solid /*[[borders]]*/!important;
}
.sidebarBox .boxFooter .boxLeft, .subMenuBox .boxFooter .boxLeft, .main .boxFooter .boxLeft {
    visibility:hidden !important;
}
.sidebarBox .boxFooter, .subMenuBox .boxFooter, .main .boxFooter, #bottom {
    -moz-border-radius-bottomleft : 9px !important;
    -webkit-border-bottom-left-radius : 9px !important;
    -moz-border-radius-bottomright : 9px !important;
    -webkit-border-bottom-right-radius : 9px !important;
}
/* 
 *********
 * FORUM *
 *********
 */
 .cfWrapper {
    border: 1px solid /*[[background]]*/ !important;
}
.cfHeader {
    background: /*[[background]]*/ !important;
    color: #fff !important;
}
.cfHeader a {
    color: white !important;
}
.cfHeader a:hover {
    color: #D2691E !important;
    text-decoration:none!important;
}
/* 
 ************
 * FOXTRICK *
 ************
 */
 #ft_bonuswage {
    color:#D2691E!important;
    font-weight:bold!important;
}
#ft_deadline {
    color:#D2691E!important;
}
span.highlight {
    color:#D2691E!important;
}
#ft_rapidid a {
    color:#ccc!important;
}
#ft_rapidid a {
    text-decoration:none!important;
    font-weight:bold!important;
    font-size:larger!important;
}
#ft_rapidid {
    margin-top:10px!important;
}
#ft_rapidid_form {
    margin-top:-5px!important;
}
#ft_rapidid a:after {
    content:", "!important;
}
#ft_rapidid_input_senior-team {
    height:11px!important;
}
#ft_rapidid a:hover {
    color:#fff!important;
}
/* 
 *****************
 * FORMATION FIX *
 *****************
 */

/*sostituzioni-condizioni avanzate*/
 a[id*="advancedLink"] {
    color:yellow!important;
    text-decoration:none!important;
    font-weight:bold!important;
    right:11px!important;
}
/*formazioni salvate */
 a#editLineupDone {
    color:red!important;
    font-weight:bold!important;
}
/*tab*/
 div#order_tabs ul.ui-tabs-nav li a {
    background:none repeat scroll 0 0 #3F7137!important;
}
div#order_tabs ul.ui-tabs-nav li:hover a {
    background:none repeat scroll 0 0 #457D3C!important;
}
div#order_tabs ul.ui-tabs-nav li.ui-tabs-selected a {
    background:none repeat scroll 0 0 #40823D!important;
    color:yellow!important;
}
div#order_tabs ul.ui-tabs-nav li {
    border : 2px solid #79A777!important;
    border-bottom:0px!important;
}
div#order_tabs ul.ui-tabs-nav li a {
    width:120px;
    height:16px!important;
}
div#order_tabs ul.ui-tabs-nav li:hover {
    border : 2px solid #79A777!important;
    border-bottom:0px!important;
}
div#order_tabs ul.ui-tabs-nav li a {
    color:white!important;
}
div#order_tabs ul.ui-tabs-nav li:hover a {
    color:white!important;
}
div#order_tabs ul.ui-tabs-nav li {
    border : 2px solid #79A777!important;
    border-bottom:0px!important;
}
#details {
    top:320px!important;
    min-height:200px!important;
    left:auto!important;
    right:134px!important;
}



/*
 NEW
*/
.switchClubTabWrapper a {
 height: auto !important;
}
.switchClubTabWrapper {
 height: auto!important;
 margin-bottom: inherit!important;
 margin-top: inherit!important;   
 width: auto!important;
}
  
}
