css:
@-moz-document domain("www.youtube.com"), domain("youtube.com") {
#like-bar.ytd-sentiment-bar-renderer { background: /*[[likecolour]]*/!important; } /* Like bar */
#container.ytd-sentiment-bar-renderer { background: /*[[dislikecolour]]*/!important; } /* Dislike bar */
ytd-menu-renderer ytd-toggle-button-renderer.style-default-active[is-icon-button]:nth-of-type(1) { color: /*[[likecolour]]*/; } /* Liked button */
ytd-menu-renderer ytd-toggle-button-renderer.style-default-active[is-icon-button]:nth-of-type(2) { color: /*[[dislikecolour]]*/; } /* Disliked button */
}
