css:
@-moz-document domain("warframe.market") {
/*backgrounds/misc styling*/
.attribute-seeker__placeholder,
.auction-entry,
.chat__block,
.chat__controls,
.content__body,
.content__header--second,
.content__header--transparent,
.content__header,
.flex--left,
.flex--right,
.floating-menu__section,
.floating-menu,
.header,
.item_orders,
.item-statistics__header,
.messenger__chat_list,
.not-found,
.odd,
.order-row,
.order-unit,
.review__zero,
.root-menu-button > .combobox,
.root-menu-button > a,
.root-menu-button > ul,
.row,
.slideout-panel,
.statistics__header,
.statistics__numbers,
.user__name,
.verification__tutorial li:nth-child(2n) > div,
.widget-modal__content,
.widget-order__content,
.widget-order__top-sell > ul > li,
input{
    background: #282222 !important;
}
.order__own_line,
.order__own_line:nth-child(2n+1),
.order__own_line > .user__name,
.widget-order__header,
.input-group-addon,
.compose__box,
.shift-5 > button:active,
.widget-modal__header,
.input-group-text{
    background: #13272A !important;
}
.seeker__dropdown,
.root-menu-button > ul li.online-switch > span.active,
.attribute-seeker__dropdown,
.item-seeker__dropdown {
    background: #000 !important;
}
/*dark-green*/
.btn__radio.active,
.btn__radio:active,
.btn__radio:focus,
.active.btn__action,
.alert-success,
.input-group-addon,
.selected-unit > span,
.review__block.positive,
.review__block.positive > p{
    background: #325743 !important;
}
.btn__light.red,
.btn__light.red > span,
.disclaimer{
    background: #8c3e3a !important;
    color: #fdfdfd !important;
}
/*Highlight items*/
.seeker,
.seeker__input,
.item-seeker,
.attribute-seeker,
.form-control, 
select,
select option,
textarea{
    background: /*[[hc1]]*/ !important;
}
.auction-entry__footer{
    background: none !important;
}
.order-unit,
.infinite-translate > div,
.widget-order__top-sell > ul > li {
    border-top: 1px solid #9d9d9d !important;
}
.btn,
.btn__light {
    border: 1px solid #fff !important;
}
.chart__donchian{
    fill: /*[[hc1]]*/ !important;
    stroke: rgba(255, 255, 255, .5) !important;
}
.group__core text{
    fill: #ffffff !important;
}
.group__legend > rect,
.group__controls > rect{
    fill: #434343
}
.auction-entry > .wfm-icon,
#icon-platinum{
    fill: #f9eaa7 !important;
}
#icon-infinity{
    fill: #ffffff !important;
}
.chart__sma-price{
    stroke: #f5f5f5 !important;
}

/*text*/
body,
h1,
.row,
.user__name,
span,
h2,
a,
label,
input{
    color: /*[[hc1]]*/ !important;
}
.order__price,
.sell_color > span,
.order__price > span, 
.buy_color > span,
.price{
    color: #f9eaa7 !important;
}
select,
select option,
textarea,
.disclaimer,
#orderItemPrice,
#orderQuantity,
#auction-create__startingPrice,
#auction-create__buyoutPrice,
#auction-create__minReputation{
    color: #000 !important;
}
::placeholder,
.attribute-seeker__placeholder{
    color: #737373 !important;
}

/*mechanical*/
footer {
    bottom: 0px !important;
}
.col-12.col-sm-7.col-md-7.col-lg-6 > .row > :nth-child(2){
    left: 130px !important;
}
.fetch-progress-bar,
#panel,
#warframe_react > #panel > .header{
    top: 40px !important;
}
.content {
    top: -255px !important
}
.user__profile,
.messenger_page,
.settings__page,
.ducanator {
    top: -199px !important;
}
/*avatar div*/
.user__profile > header > .container > .row > :nth-child(1) > .row > .profile__avatar{
    padding: 0px !important;
    top: -30px !important;
}
.auctions__page{
    display: inline !important;
    top: -415px !important;
}
.footer__body--left {
    flex-grow: 4 !important;
}
.floating-menu,
.floating-menu__section,
.root-menu-button > .combobox,
.floating-menu__section,
.root-menu-button > a {
    height: 40px !important;
}
.profile__stats{
    margin-left: 140px !important;
}
.ducanator.content,
.messenger_page.content{
    margin-top: 20px !important;
}
.user__profile.content{
    margin-top:70px
}
.shift-5 {
    margin-top: -201px !important;
}
.item__name {
    position: fixed !important;
    margin-top: 310px !important;
    margin-left: auto !important;
}
.auctions__page > .content_header,
.auction-entry{
    border-top: 1px solid #f1f1f1 !important;
    margin: 0px -15px !important;
}
.item__set,
.item__root_icon {
    position: fixed !important;
    margin-top: 210px !important;
    width: 30% !important;
}
.mt-3,
.my-3,
.mt-4{
    margin-top: 0rem !important;
    margin-bottom: 0rem !important;
    padding-top: 1rem !important;
    padding-bottom: 1rem !important
}
.header.size--most_recent {
    min-height: 0vh !important;
}
.footer__body--left > div {
    padding: 8px !important;
}
.user__profile > header > .container > .row > :nth-child(1) > .row > :nth-child(2){
    padding: 0px !important;
}
.header__body{
    margin-bottom: 265px !important;
}
#panel > .content,
.content__header--second flex--root{
    padding-top: 40px !important;
}
.content__header > .container > .row > :nth-child(2){
    flex: 0 0 30%;
    line-height: 1px !important;
    max-width: 30% !important;
    padding: 0px !important;
    position: absolute !important;
    right: 5vw !important;
}
.content__header > .container > .row > :nth-child(2) > .row{
    display: inline !important;
    min-width: 50px !important;
    max-width: 100px !important;
}
.profile__stats {
    flex: 0 0 50%;
    max-width: 40% !important;
}
.item__attribute{
    max-width: 300px !important;
}


/*hidden*/
.google__badge,
.ios__badge,
.logotype,
.footer__adpat,
.ad__line,
.ad__unit,
.menu-button,
#parallaxier,
.parallaxier {
    display: none !important;
}

/*not hidden*/
.top-menu-right,
.platform-menu{
    display: flex !important;
}
}
