css:
@-moz-document url-prefix("https://duckduckgo.com/") {

      div[data-hostname='www.google.de'] {
          display: none;
      }

      div[data-hostname='www.google.com'] {
          display: none;
      }

      div[data-hostname='www.faz.net'] {
          display: none;
      }

      div[data-hostname='answers.microsoft.com'] {
          background-color: red;
      }

      div[data-hostname='stackoverflow.com'] {
          background-color: green;
      }

      div[data-hostname='developercommunity.visualstudio.com'] {
          background-color: orange;
      }
}
