@-moz-document url-prefix("https://leerling.studyflow.nl/") {
.feedback-button {
    visibility: visible;
    opacity: 0.5;
    border-radius: 3px;
    height: 30px;
    padding: 0 18px;
    line-height: 29px;
    display: inline-block;
    position: fixed;
    left: 12px;
    bottom: 12px;
    background: #730000 !important;
    cursor: progress;
    color: white;
    font-size: 13px;
    font-weight: 500;
    z-index: 600;
}



.top-bar .top-bar-course, .top-bar .top-bar-department {
    float: left;
    position: relative;
    height: 30px;
    margin-top: 13px;
    border-radius: 3px;
    background-color: #730000 !important;
    padding: 0 18px;
    margin-left: 40px;
    cursor: progress !important;
}

.top-bar {
    transition: top, ease-out, 0.15s;
    left: 0;
    right: 0;
    top: 0;
    height: 55px;
    position: fixed;
    z-index: 800;
    background: #b10808 !important;
    padding: 0 16px;
    font-size: 15px;
}


/*dit is het top logo*/


.top-bar .logo {
    background: url(https://img.fireden.net/v/image/1471/02/1471025750923.gif) no-repeat center !important;
    width: 55px;
    height: 55px;
    float: left;
    height: 55px;
}

/*hier boven!!*/

body {
font-family: sans-serif !important;
    background: url("/*[[bg]]*/") #1C1C1C bottom fixed no-repeat !important; 
	background-size: cover !important; 	/*niet wegdoen*/
    color: #000 !important;
}    

.chapter-title {
    font-size: 14px;
    color: #ffffff !important;
    text-align: center;
    width: 100%;
    padding: 13px 20px;
    line-height: 22px;
    margin: 0;
    font-weight: 500;
    box-sizing: border-box;
    background-color: #ffffff !important;
    background-image: linear-gradient(#ad0000, #bb0000) !important;
    border-bottom-right-radius: 10px;
    border-bottom-left-radius: 10px;
    text-shadow: 0 1px #ffffff !important;
}

.chapter-icon {
    height: 84px;
    border-top-right-radius: 6px;
    border-top-left-radius: 6px;
    background: #383636 !important;
    display: block;
    text-align: center;
}

.knowledge-chapter-progress-outer {
    background: #292828 !important;
    padding: 6px 6px;
    border-bottom: 1px solid #232323;
}




/* dit is de aanwijzer waar jen bent!!!!*/


.chapter .chapter-icon.recommended::before {
    content: "";
    width: 50px !important;
    height: 50px !important;
  background: url(https://static.planetminecraft.com/files/avatar/1150996_12.gif);
    position: absolute;
    left: 50%;
    margin-left: -30px !important;
    top: 0px;
    z-index: 99;
    pointer-events: none;
    -webkit-animation: location-updown 1.6s infinite;
    animation: location-updown 1.6s infinite;
    -webkit-animation-timing-function: ease;
    animation-timing-function: ease;
    -webkit-transform: translateY(-37px);
    transform: translateY(-37px);
}

.chapter-progress-outer {
    height: 30px;
    background: #292828 !important;
    padding: 10px 12px;
    padding-right: 13px;
    box-sizing: border-box;
    border-bottom: 1px solid #292828;
}

.chapter-nav {
    width: 300px;
    position: fixed;
    z-index: 500;
    left: -300px;
    top: 55px;
    bottom: 0;
    background: #292828 !important;
    border-right: 1px solid #292828;
}

.chapter-nav .chapter-title {
    text-overflow: ellipsis;
    overflow: hidden;
    white-space: nowrap;
    box-shadow: 0 1px 1px rgba(0, 0, 0, 0.15);
    border-radius: 0;
    background-color: #F8F8F8;
    background-image: none;
    border-bottom: 1px solid #000000;
    height: 51px;
    padding-top: 15px;
}
  
  .chapter-nav-overlay {
    background: #2b2b2b;
    position: fixed;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    visibility: hidden;
    opacity: 0;
    z-index: 300;
    transition: opacity, linear, 0.15s;
}
  
 .avatar-gold {
    position: absolute;
    right: 15px;
    top: 15px;
    background: url(data:image/jpeg;base64,/9j/4AAQSkZJRgABAQAAAQABAAD/2wCEAAkGBxMTEhUSEhMVFRUXâ€¦dpVpdh0HTqyEF2IBQVApeJiVRmYPDumZTlBsZhAyNYaWXrxT1QAKiCQKSAHMwid5LGx2MMH//Z) no-repeat right 4px center rgba(0, 0, 0, 0.4);
    background-size: 18px;
    padding-right: 25px;
    padding-left: 8px;
    box-sizing: border-box;
    line-height: 23px;
    text-align: right;
    color: rgba(255, 251, 0, 0.8);
    border-radius: 3px;
    font-size: 12px;
    font-weight: 500;
}

div.logo::after {
    position: relative;
    font-family: Arial;
    margin-left: 10px;
    font-size: 11px;
    width: 300px;
    box-sizing: border-box;
    content: "Neuk mijn Moeder, 50 Eulo";
    font-weight: bold;
    padding: 5px;
    background-color: var(--button-hover);
    border-radius: 20px;
}

.stars {
    position: relative;
    float: right;
    width: 37px;
    height: 37px;
    background-color: #730000;
    border-radius: 50%;
    margin-top: 9px;
    position: relative;
}


.content-block {
    margin: 50px auto;
    max-width: 700px;
    background: #404040;
    border-radius: 6px;
    transition: margin, ease-out, 0.15s;
    position: relative;
    border: 1px solid #424242;
    box-sizing: border-box;
}


p, .div-p {
    color: #ffffff;
    font-weight: 400;
    font-size: 15px;
    line-height: 26px;
    margin-bottom: 20px;
}

.question-controls {
    background: #5f5f5f;
    clear: both;
    margin: 40px -40px -40px -40px;
    border-bottom-right-radius: 6px;
    border-bottom-left-radius: 6px;
    border-top: 1px solid #525252;
    padding: 22px 40px;
    height: 84px;
    box-sizing: border-box;
}

.content-header {
    border-top-right-radius: 6px;
    border-top-left-radius: 6px;
    background: linear-gradient(#404040, #525151);
    height: 55px;
    margin-bottom: -105px;
    border-bottom-right-radius: 0px;
    border-bottom-left-radius: 0px;
    border: 1px solid #404040;
    border-bottom: 1px solid #4a4949;
    box-sizing: border-box;
    position: relative;
    z-index: 100;
    padding: 12px 16px;
}

.btn {
    height: 40px;
    padding: 0 18px;
    line-height: 38px;
    outline: none;
    text-decoration: none;
    overflow: hidden;
    white-space: nowrap;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    box-sizing: border-box;
    position: relative;
    display: inline-block;
    text-align: center;
    cursor: cell;
    font-weight: 500;
    font-size: 14px;
    color: #000000;
    border-radius: 3px;
    border: 1px solid #000000;
    box-shadow: 0px 1px 2px 0px rgb(29, 29, 29);
    background: linear-gradient(-180deg, #292929 0%, #212121 100%);
    -webkit-user-select: none;

}


}
