css:
@-moz-document domain("komputeko.net") {
    body {
        background-color: #222;
        color: azure;
        font-family: 'Fira Code', Consolas, monospace;
    }
    a {
        color: darkgreen;
        text-shadow: 1px 1px 2px black;
    }
    td p {
        font-size: 1.2rem;
    }
    TABLE.search TD {
        background-color: #333;
    }
    TABLE.search .second TD {
        background-color: #444
    }
    span[style] {
        background: #233 !important;
    }
    TABLE.search TH {
        background-color: #222;
    }
    td b {
        background-color: #222;
    }
    #sercxrezultoj {
        background-color: #222;
    }
    #vorto {
        background-color: #444;
        color: azure;
        font-family: 'Fira Code', Consolas, monospace;
    }
    body>div:nth-child(1)>table:nth-child(1)>tbody:nth-child(1)>tr:nth-child(1)>td:nth-child(1)>form:nth-child(3)>input:nth-child(2), body>table:nth-child(1)>tbody:nth-child(1)>tr:nth-child(1)>td:nth-child(2)>form:nth-child(1)>input:nth-child(2) {
        background-color: #333 !important;
        color: azure;
        font-family: 'Fira Code', Consolas, monospace;
    }
    body>div:nth-child(1)>table:nth-child(1)>tbody:nth-child(1)>tr:nth-child(1)>td:nth-child(1)>a:nth-child(1)>img:nth-child(1), body>div:nth-child(1)>table:nth-child(5)>tbody:nth-child(1)>tr:nth-child(1)>td:nth-child(1)>a:nth-child(1)>img:nth-child(1), img {
        filter: invert(80%);
        /*filter: grayscale(100%);*/
    }
    body>div:nth-child(1)>table:nth-child(7)>tbody:nth-child(1)>tr:nth-child(1)>td:nth-child(1)>center:nth-child(4) {
        transform: scale(.5);
    }
    center img {
        padding: 0 5px 0 5px;
    }
}
