@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hattrick.ws"), domain("hattrick.interia.pl"), domain("hattrick.uol.com.br"), domain("hattrick.org") {

/*main*/
.hattrick, .hattrickNoSupporter, #page { width: /*[[w]]*/ !important; min-width:990px !important; }
div#content  { padding-right:1% !important; padding-left:1% !important; margin-right: 0%!important;
min-width:970px!important;}
div#content > div.mainWrapperRegular, 
div#content > div.mainWrapperWide,
div#content > div#mainWrapper:not(.mainWrapperConf){ width: 79% !important; padding-left:0% !important;padding-right:0% !important; margin-right: 0%!important;}
.main,.mainRegular { width: 74% !important;
 padding-right:1% !important; padding-left:0% !important; margin-right: 0%!important;}
.mainWide { min-width:100% !important }
.subMenu { width: 20% !important;margin-right:1%!important; }
#sidebar { width: 25% !important; }
.mainWrapperFull,.mainFull {width: 100%!important;}

.switchClubTab{border-radius:0!important;}


/* header */
#menu { padding: 0px !important;margin-right: 1% !important; margin-left:1% !important; 
    border-radius: 0;
    left: 0;
    right: 0;
    width: auto;
    letter-spacing: 1px;
    padding-right: 11px;
}
.switchClubTabWrapper,#footer{width: 100% !important;}
#header,#top,#content,#bottom {background:#dce2dc;border: 0px;}


/* header 
#ticker { padding-left: 150px !important; }
#shortcuts { right: 25px !important; }
#time { margin-right: 10px !important; }*/


/*conference*/
.mainWrapperConf { width: 70%  !important; }
.subMenuConf { width: 29%  !important; margin-right:1%!important;}
.mainConf { width: 100%  !important; }

/* match 
div.mainWrapperWide { width: 980px !important; }
div.mainWide { width: 970px !important; }*/

/*transfert*/
div[id$=_TransferPlayer_plPlayerInfo]{width: 100%!important;}
div.transferPlayerInformation{width: 50%!important;}
div.transferPlayerSkills{width: 50%!important;padding-left:0%!important;}

/*tattico*/
.mainFull{width: 100%  !important;}

/*shop*/
.mainEqualLibrium{width: 100%  !important;}

.threadItem > td > div.url,
.threadItem > td > div.urlShort ,
.threadItem > td > div.url > a, 
.threadItem > td > div.urlShort > a {
    width: 100%!important;
}

}

