css:
@-moz-document domain("gameprogrammingpatterns.com") {
  body {
    background: #111;
  }

  .page {
    background: #111;
    border: none;
  }

  .content {
    background: #222;
    color: #fff;
  }

  pre {
    color: #eee;
    background: #0c2730;
  }

  aside {
    border-left: solid 8px #144251;
  } 

  h2 {
    border-left: solid 8px #198bb0;
  }

  nav {
    background: #3e484d;
    color: #bddfef;
  }

  a {
    color: #2eb9ff;
  }

  code {
    color: #00c1ea;
  }

  blockquote {
    background: #330069;
  }

  blockquote p {
    color: #caeeff;
  }
}
