css:
@-moz-document domain("discordapp.com"), domain("discord.com") {
    [class^="spoilerText"] [class^="inlineContent"] {
        opacity: 1 !important;
        pointer-events: auto !important;
    }
    [class^="imageWrapper"], [class*="spoilerEmbed"] [class*="embedInner"] {
        filter: none !important;
    }
    [class^="spoilerWarning"] {
        display: none;
    }
    [class*="hiddenSpoilers"] {
        visibility: visible !important;
    }
}
