css:
@-moz-document domain("canvas.tue.nl") {
a, .btn-link:hover, .Button--link {
    color: var(--ic-brand-primary)
}
:root {
    --ic-brand-global-nav-bgd: var(--ic-brand-primary);
    --ic-brand-global-nav-logo-bgd: var(--ic-brand-primary);
    --ic-brand-primary-darkened-5: #BE1818;
    --ic-brand-primary-darkened-10: #B41717;
    --ic-brand-primary-darkened-15: #AA1616;
    --ic-brand-primary-lightened-5: #CA2424;
    --ic-brand-primary-lightened-10: #CD3030;
    --ic-brand-primary-lightened-15: #D03B3B;
    --ic-brand-button--primary-bgd-darkened-5: #BE1818;
    --ic-brand-button--primary-bgd-darkened-15: #AA1616;
    --ic-brand-button--secondary-bgd-darkened-5: #000000;
    --ic-brand-button--secondary-bgd-darkened-15: #000000;
    --ic-brand-font-color-dark-lightened-15: #AA1616;
    --ic-brand-font-color-dark-lightened-30: #AA1616;
    --ic-link-color-darkened-10: #B41717;
    --ic-link-color-lightened-10: #CD3030;
    --ic-brand-primary: #c81919;
    --ic-brand-font-color-dark: #2D3B45;
    --ic-link-color: #c81919;
}
.unread-grade {
    background-color: var(--ic-brand-primary)
}
}
