css:
@-moz-document domain("bogleheads.org") {
body,
table {
    background-color: #000;
    color: #fff;
}

tbody,
blockquote,
blockquote blockquote blockquote {
    background-color: #000;
}

blockquote blockquote,
.post,
.navbar,
blockquote blockquote blockquote blockquote {
    background-color: #32373A
}

#wrap,
#footer_table {
    background-color: #000;
}

.content {
    color: #fff;
}

.headerbar,
.forabg,
.forumbg {
    background-color: #32373A;
    background-image: none;
}

ul.forums,
ul.topics {
    background-color: #000;
    background-image: none;
}

#leftside,
#wrapper,
#rightside,
.panel {
    color: #fff;
    background-color: #000;
}

a {
    color: #FF0000
}

p.author {
    color: #fff;
}

.post:target .content {
    color: #fff;
}
}
