css:
*:not(a):not(input):not(pre):not(textarea):not(select):not(form):not(span):not(code) {
    color: #dadada !important;
    text-shadow: none !important;
    box-shadow: none !important;
    background: transparent !important;
    background-color: #333 !important;
}

*::after, *::before {
    background: transparent !important;
    /*background-color: #333 !important;*/
}

::-webkit-input-placeholder {
    color: #8aa9fd !important;
    text-shadow: none !important;
    box-shadow: none !important;
}

a {
    color: #8aa9fd !important;
    text-decoration: none !important;
}

span {
    color: #dadada !important;
    text-shadow: none !important;
    box-shadow: none !important;
}

p > span {
    background-color: transparent !important;
}

p,
a,
form {
    background-color: transparent !important;
    text-shadow: none !important;
    box-shadow: none !important;
}

form {
    padding: 0 !important;
    border: none !important;
}

img {
    border-radius: 3px !important;
}

button,
code,
input,
pre,
textarea,
select,
input[type],
code:focus,
input:focus,
pre:focus,
textarea:focus,
select:focus,
input[type]:focus,
code:hover
input:hover,
pre:hover,
textarea:hover,
select:hover,
input[type]:hover {
    background-image: none !important;
    background: transparent !important;
    background-color: #292929 !important;
    color: #dadada !important;
    border: none !important;
    border-radius: 3px !important;
    box-shadow: none !important;
    -webkit-appearance: none !important;
}

input[type]:not(input[type=submit]) {
    padding: .25em .25em .25em 2em !important
}

input[type=submit] {
    padding: .25em;
    color: #8aa9fd !important;
}

a:hover,
input[type=submit]:hover,
select:hover {
    color: #dadada !important;
    cursor: pointer;
}

select {
    color: #8aa9fd !important;
}
