css:
@-moz-document url(http://www.furvilla.com/inventory), url-prefix(http://www.furvilla.com/inventory?) {
#stack-form a.btn {
    width: 66px !important;
    position: absolute;
    margin-left: -65px;
}

#stack-form a.btn.pull-left {
    width: 100px !important;
    position: relative;
    margin-left: 0px;
}

#stack-form {
    margin-left: 60px;
}

#stack-form span.sod_select {
    margin-left: -60px;
    width: 507px;
}
}

@-moz-document url-prefix(http://www.furvilla.com/inventory/) {
#stack-form a.btn {
    width: 66px !important;
    position: absolute;
    margin-left: -65px;
}

#stack-form a.btn.pull-left {
    width: 100px !important;
    position: relative;
    margin-left: 0px;
}

#stack-form {
    margin-left: 60px;
}

#stack-form span.sod_select {
    margin-left: -60px;
    width: 716px;
}
}
